// In src/pages/LoginPage.js

import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function LoginPage({ onLogin }) { // Receives onLogin function as a prop
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = (event) => {
    event.preventDefault();
    axios.post('http://127.0.0.1:8000/api/auth/login/', {
      username: username,
      password: password,
    })
    .then(response => {
      console.log('Login successful:', response.data);
      const token = response.data.key; // The token is in the 'key' field
      onLogin(token); // Pass the token up to the App component
      navigate('/'); // Redirect to the home page
    })
    .catch(error => {
      console.error('Login error!', error.response.data);
    });
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div><input type="text" value={username} onChange={e => setUsername(e.target.value)} placeholder="Username" required /></div>
        <div><input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" required /></div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default LoginPage;
