import React, { useContext } from 'react';
import { Navigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const NonAdminRoute = ({ children }) => {
  const { isAuthenticated, userGroups } = useContext(AuthContext);

  const isAdmin =
    userGroups?.includes('DEPARTMENT_ADMIN') ||
    userGroups?.includes('HIGHER_AUTHORITY');
  const isTriage = userGroups?.includes('TRIAGE_USER');

  const isCitizen = !isAdmin && !isTriage;

  if (!isAuthenticated) return <Navigate to="/login" />;

  return isCitizen ? children : <h2>Admins and triage users cannot access this page.</h2>;
};

export default NonAdminRoute;
