// In src/pages/LoginPage.js

import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const LoginPage = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);
    const navigate = useNavigate();
    const { login } = useContext(AuthContext);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);

        try {
            // Step 1: Log in to the correct JWT endpoint
            const response = await axios.post('http://127.0.0.1:8000/api/token/', {
                username,
                password,
            });

            const accessToken = response.data.access;
            if (!accessToken) {
                throw new Error("Login failed, access token not received.");
            }

            // Step 2: Check if the user is an admin by trying to access an admin-only endpoint
            let isStaff = false;
            try {
                await axios.get('http://127.0.0.1:8000/api/admin/grievances/', {
                    headers: { 'Authorization': `Bearer ${accessToken}` }
                });
                // If the above request does not fail, the user is staff.
                isStaff = true;
            } catch (adminCheckError) {
                // We expect a 403 Forbidden error for non-staff users. This is normal.
                if (adminCheckError.response && adminCheckError.response.status === 403) {
                    isStaff = false;
                } else {
                    // Any other error during the admin check is unexpected.
                    console.error("Admin check failed unexpectedly:", adminCheckError);
                }
            }
            
            // Step 3: Save the session and role in our context
            login({ token: accessToken, is_staff: isStaff });
            
            // Step 4: Navigate to the correct page
            if (isStaff) {
                navigate('/admin');
            } else {
                navigate('/');
            }

        } catch (err) {
            setError('Invalid credentials or server error. Please try again.');
            console.error("Login process error:", err);
        }
    };

    return (
        <div>
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
                <div><input type="text" value={username} onChange={e => setUsername(e.target.value)} placeholder="Username" required /></div>
                <div><input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" required /></div>
                <button type="submit">Login</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default LoginPage;
