// In src/components/UpdateForm.js

import React, { useState } from 'react';
import axios from 'axios';

function UpdateForm({ grievance, onUpdate, onCancel }) {
  const [title, setTitle] = useState(grievance.title);
  const [description, setDescription] = useState(grievance.description);

  const handleUpdate = (event) => {
    event.preventDefault();
    api.post('grievances/', {
      title: title,
      description: description,
      status: grievance.status, // Keep the original status
    })
    .then(response => {
      console.log('Grievance updated:', response.data);
      onUpdate(); // This will trigger a data refresh and exit edit mode
    })
    .catch(error => {
      console.error("There was an error updating the grievance!", error);
    });
  };

  return (
    <form onSubmit={handleUpdate}>
      <div>
        <label>Title: </label>
        <input
          type="text"
          value={title}
          onChange={e => setTitle(e.target.value)}
          required
        />
      </div>
      <div style={{ marginTop: '10px' }}>
        <label>Description: </label>
        <textarea
          value={description}
          onChange={e => setDescription(e.target.value)}
          required
        />
      </div>
      <button type="submit" style={{ marginTop: '10px' }}>Save</button>
      <button type="button" onClick={onCancel} style={{ marginLeft: '10px' }}>Cancel</button>
    </form>
  );
}

export default UpdateForm;
