// In src/App.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import GrievanceForm from './components/GrievanceForm'; // <-- Import the form
import './App.css';

function App() {
  const [grievances, setGrievances] = useState([]);

  // Function to fetch grievances, which we can call anytime
  const fetchGrievances = () => {
    axios.get('http://127.0.0.1:8000/api/grievances/')
      .then(response => {
        setGrievances(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the grievances!", error);
      });
  };

  // Run fetchGrievances once when the component loads
  useEffect(() => {
    fetchGrievances();
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        {/* Render the form and pass the fetch function as a prop */}
        <GrievanceForm onGrievanceCreated={fetchGrievances} />

        <hr style={{ width: '80%', margin: '40px 0' }} />

        <h1>Grievance List</h1>
        <div>
          {grievances.map(grievance => (
            <div key={grievance.id} style={{ border: '1px solid white', margin: '10px', padding: '10px' }}>
              <h2>{grievance.title}</h2>
              <p>{grievance.description}</p>
              <p>Status: {grievance.status}</p>
            </div>
          ))}
        </div>
      </header>
    </div>
  );
}

export default App;
