import React, { useContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate, useParams } from 'react-router-dom';
import { AuthContext, AuthProvider } from './context/AuthContext';

import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import AdminPage from './pages/AdminPage';
import GrievanceForm from './components/GrievanceForm';
import NonAdminRoute from './components/NonAdminRoute';
import UserGrievanceDetail from './components/UserGrievanceDetail';
import TriageDashboard from './components/TriageDashboard';
import './App.css';

function UserGrievanceDetailWrapper() {
  const { id } = useParams();
  return <UserGrievanceDetail grievanceId={id} />;
}

function ProtectedRoute({ children }) {
  const { isAuthenticated } = useContext(AuthContext);
  return isAuthenticated ? children : <Navigate to="/login" />;
}

function AdminRoute({ children }) {
  const { isAuthenticated, userGroups } = useContext(AuthContext);

  const isAdmin =
    userGroups?.includes('DEPARTMENT_ADMIN') ||
    userGroups?.includes('HIGHER_AUTHORITY');

  return isAuthenticated && isAdmin ? children : <Navigate to="/" />;
}


function AppContent() {
  const { isAuthenticated, userGroups, logout } = useContext(AuthContext);

  const isAdmin = userGroups?.includes('DEPARTMENT_ADMIN') || userGroups?.includes('HIGHER_AUTHORITY');
  const isTriage = userGroups?.includes('TRIAGE_USER');
  const isCitizen = !isAdmin && !isTriage;

  return (
    <div className="App">
      <nav className="main-app-nav" style={{ marginBottom: '20px', fontSize: '1.2rem' }}>
        {isAuthenticated ? (
          <>
            {isCitizen && (
              <Link to="/" style={{ marginRight: '15px' }}>Home</Link>
            )}
            {isAdmin && (
              <Link to="/admin" style={{ marginRight: '15px', color: 'cyan' }}>Admin</Link>
            )}
            {isTriage && (
              <Link to="/triage" style={{ marginRight: '15px', color: 'orange' }}>Triage Dashboard</Link>
            )}
            <button onClick={logout}>Logout</button>
          </>
        ) : (
          <>
            <Link to="/login" style={{ marginRight: '15px' }}>Login</Link>
            <Link to="/register">Register</Link>
          </>
        )}
      </nav>

      <Routes>
        {/* public */}
        <Route path="/login" element={<LoginPage />} />
        <Route path="/register" element={<RegisterPage />} />

        {/* citizen only (non‑admin, non‑triage) */}
        <Route path="/" element={
          <ProtectedRoute>
            <NonAdminRoute>
              <HomePage />
            </NonAdminRoute>
          </ProtectedRoute>
        } />
        <Route path="/submit-grievance" element={
          <ProtectedRoute>
            <NonAdminRoute>
              <GrievanceForm />
            </NonAdminRoute>
          </ProtectedRoute>
        } />
        <Route path="/user/grievances/:id" element={
          <ProtectedRoute>
            <NonAdminRoute>
              <UserGrievanceDetailWrapper />
            </NonAdminRoute>
          </ProtectedRoute>
        } />

        {/* admin (DEPARTMENT_ADMIN + HIGHER_AUTHORITY) */}
        <Route path="/admin" element={
          <AdminRoute>
            <AdminPage />
          </AdminRoute>
        } />

        {/* triage only */}
        <Route path="/triage" element={
          <AdminRoute>
            <TriageDashboard />
          </AdminRoute>
        } />
      </Routes>
    </div>
  );
}


function App() {
  return (
    <AuthProvider>
      <Router>
        <AppContent />
      </Router>
    </AuthProvider>
  );
}

export default App;
