// In src/pages/LoginPage.js

import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api';

function LoginPage({ onLogin }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = (event) => {
    event.preventDefault();
    api.post('auth/login/', {
      username: username,
      password: password,
    })
    .then(response => {
      console.log('Login successful:', response.data);
      // First, update the global state in App.js
      onLogin(response.data);
      
      // vvv THIS IS THE CHANGE vvv
      // Now, check the is_staff flag from the response and redirect
      if (response.data.is_staff) {
        navigate('/admin'); // If user is staff, go to the admin page
      } else {
        navigate('/'); // Otherwise, go to the regular home page
      }
    })
    .catch(error => {
      console.error('Login error!', error.response ? error.response.data : 'No response');
      alert('Login failed. Please check your username and password.');
    });
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div><input type="text" value={username} onChange={e => setUsername(e.target.value)} placeholder="Username" required /></div>
        <div><input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" required /></div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default LoginPage;
