import React, { useContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { AuthContext, AuthProvider } from './context/AuthContext';

import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import AdminPage from './pages/AdminPage';
import GrievanceForm from './components/GrievanceForm';

import NonAdminRoute from './components/NonAdminRoute'; // <-- new import
import UserGrievanceDetail from './components/UserGrievanceDetail';
import './App.css';

import { useParams } from 'react-router-dom';

function UserGrievanceDetailWrapper() {
  const { id } = useParams();
  return <UserGrievanceDetail grievanceId={id} />;
}

// Protects routes for authenticated users
function ProtectedRoute({ children }) {
  const { isAuthenticated } = useContext(AuthContext);
  return isAuthenticated ? children : <Navigate to="/login" />;
}

// Protects routes for staff users
function AdminRoute({ children }) {
  const { isAuthenticated, isStaff } = useContext(AuthContext);
  return isAuthenticated && isStaff ? children : <Navigate to="/" />;
}

function AppContent() {
  const { isAuthenticated, isStaff, logout } = useContext(AuthContext);

  return (
    <div className="App">
      <nav style={{ marginBottom: '20px', fontSize: '1.2rem' }}>
        {isAuthenticated ? (
          <>
            {/* Hide Home and Grievance links for admins */}
            {!isStaff && (
              <>
                <Link to="/" style={{ marginRight: '15px' }}>Home</Link>
                <Link to="/submit-grievance" style={{ marginRight: '15px' }}>Submit Grievance</Link>
              </>
            )}
            {isStaff && (
              <Link to="/admin" style={{ marginRight: '15px', color: 'cyan' }}>Admin</Link>
            )}
            <button onClick={logout}>Logout</button>
          </>
        ) : (
          <>
            <Link to="/login" style={{ marginRight: '15px' }}>Login</Link>
            <Link to="/register">Register</Link>
          </>
        )}
      </nav>
      <Routes>
        {/* Public routes */}
        <Route path="/login" element={<LoginPage />} />
        <Route path="/register" element={<RegisterPage />} />


        {/* Protected user routes, completely blocked for admin */}
        <Route path="/submit-grievance" element={
          <ProtectedRoute>
            <NonAdminRoute>
              <GrievanceForm />
            </NonAdminRoute>
          </ProtectedRoute>
        } />
        <Route path="/" element={
          <ProtectedRoute>
            <NonAdminRoute>
              <HomePage />
            </NonAdminRoute>
          </ProtectedRoute>
        } />

        {/* Admin only route (admin dashboard) */}
        <Route path="/admin" element={
          <AdminRoute>
            <AdminPage />
          </AdminRoute>
        } />
      </Routes>
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <Router>
        <Route path="/user/grievances/:id" element={
  <ProtectedRoute>
    <NonAdminRoute>
      <UserGrievanceDetailWrapper />
    </NonAdminRoute>
  </ProtectedRoute>
} />
        <AppContent />
      </Router>
    </AuthProvider>
  );
}


export default App;
