import React, { useState, useEffect } from 'react';
import apiClient from '../apiClient';
import { useNavigate } from 'react-router-dom';
import Select, { components } from 'react-select';

// Utility: Recursively flatten leaf subcategories
function getLeafCategories(categories) {
  if (!categories) return [];
  let leaves = [];
  categories.forEach(cat => {
    if (cat.subcategories && cat.subcategories.length > 0) {
      leaves = leaves.concat(getLeafCategories(cat.subcategories));
    } else {
      leaves.push(cat);
    }
  });
  return leaves;
}

const GrievanceFormContent = () => {
  const [description, setDescription] = useState('');
  const [categoryId, setCategoryId] = useState('');
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [location, setLocation] = useState('');
  const [images, setImages] = useState([]);
  const navigate = useNavigate();

  // New states for AI auto-suggestion
  const [suggestedCategories, setSuggestedCategories] = useState([]);

const relevantOptions = (
  description.length < 8 || suggestedCategories.length === 0
    ? leafCategories.filter(cat => cat.name.trim().toLowerCase() !== 'other')
    : suggestedCategories
        .map(name => categories.find(cat => cat.name === name))
        .filter(cat => cat && cat.name.trim().toLowerCase() !== 'other')
);

// Merge in "Other" always at the end
const selectOptions = [
  ...relevantOptions.map(cat => ({
    value: cat.id,
    label: cat.full_path || cat.name,
    tooltip: `More info about '${cat.full_path || cat.name}'`
  })),
  ...categories
    .filter(cat => cat.name.trim().toLowerCase() === 'other')
    .map(cat => ({
      value: cat.id,
      label: cat.full_path || cat.name,
      tooltip: 'Use this if none above matches your issue'
    }))
];

// Custom Option with real tooltip
const CustomOption = props => (
  <div title={props.data.tooltip}>
    <components.Option {...props} />
  </div>
);

  // Fetch Leaf Categories on Mount
  useEffect(() => {
    const fetchData = async () => {
      try {
        const catResponse = await apiClient.get('categories/');
        setCategories(catResponse.data || []);
      } catch (err) {
        setError('Failed to load form data. Please refresh the page.');
      }
    };
    fetchData();
  }, []);

  // Flatten leaf categories and put "Other" at the end
  const leafCategories = getLeafCategories(categories);
  
  // Name-to-ID lookup for auto-selecting
  const catNameToId = {};
  leafCategories.forEach(cat => {
    catNameToId[cat.name.trim()] = cat.id;
  });

  // AI Suggestion useEffect
  useEffect(() => {
  const fetchSuggestions = async () => {
    setSuggestedCategories([]);
    if (description.length < 8) return;   // Show all categories if not enough text

    try {
      const res = await apiClient.post('suggest-type/', { description });
      if (res.data && res.data.suggestions) {
        setSuggestedCategories(res.data.suggestions);
      }
    } catch (err) {
      setSuggestedCategories([]);
    }
  };
  fetchSuggestions();
  // eslint-disable-next-line react-hooks/exhaustive-deps
}, [description]);



  const isReadyToSubmit = !!categoryId && !!description;

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('description', description);
    formData.append('category', String(categoryId));
    if (location) {
      formData.append('location', location);
    }
    images.forEach(file => formData.append('images', file));

    try {
      await apiClient.post('grievances/', formData, { headers: { 'Content-Type': 'multipart/form-data' } });
      setImages([]);
      navigate('/');
    } catch (err) {
      if (err.response && err.response.data) {
        const errorMessages = Object.entries(err.response.data)
          .map(([field, messages]) =>
            `${field}: ${Array.isArray(messages) ? messages.join(' ') : messages}`
          )
          .join(' | ');
        setError(errorMessages);
      } else {
        setError('An unexpected error occurred.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleImagesChange = (e) => {
    const newFiles = Array.from(e.target.files);
    setImages(prevImages => {
      const existingFiles = prevImages.map(f => f.name + f.size);
      const uniqueNewFiles = newFiles.filter(f => !existingFiles.includes(f.name + f.size));
      // Max 3 images
      const mergedFiles = [...prevImages, ...uniqueNewFiles].slice(0, 3);
      return mergedFiles;
    });
  };

  const removeImage = idx => setImages(prev => prev.filter((_, i) => i !== idx));

  return (
    <div className="form-container">
      <h2>Submit a New Grievance</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="description">Describe your complaint</label>
          <textarea
            id="description"
            value={description}
            onChange={e => setDescription(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="category">Select the complaint type</label>
          <select
            id="category"
            value={categoryId}
            onChange={e => setCategoryId(e.target.value)}
            required
            style={{ minWidth: 220 }}
          >
            <option value="">Select a complaint type...</option>
            {(
              // If nothing is typed, or suggestions list is empty, show ALL (except Other)
              description.length < 8 || suggestedCategories.length === 0
                ? leafCategories.filter(cat => cat.name.trim().toLowerCase() !== 'other')
                // Otherwise, show only suggestions (except "Other" if included)
                : suggestedCategories
                    .map(name => categories.find(cat => cat.name === name))
                    .filter(cat => cat && cat.name.trim().toLowerCase() !== 'other')
            ).map(cat =>
              <option key={cat.id} value={cat.id}>
                {cat.full_path || cat.name}
              </option>
            )}
            {/* "Other" always appears at the end */}
            {categories
              .filter(cat => cat.name.trim().toLowerCase() === 'other')
              .map(cat =>
                <option key={cat.id} value={cat.id}>
                  {cat.full_path || cat.name}
                </option>
              )
            }
          </select>



        </div>
        <div className="form-group">
          <label htmlFor="location">Location or Address</label>
          <input
            id="location"
            type="text"
            value={location}
            onChange={e => setLocation(e.target.value)}
            placeholder="e.g., 123 Main St, near water tank"
          />
        </div>
        <div className="form-group">
          <label htmlFor="resolution_images">Upload images (optional, max 3)</label>
          <input
            type="file"
            id="resolution_images"
            accept="image/*"
            multiple
            onChange={handleImagesChange}
            disabled={images.length >= 3}
          />
          {images.length >= 3 && (
            <div style={{ color: 'red', marginTop: '4px' }}>
              You can upload up to 3 images only.
            </div>
          )}
          {images.length > 0 && (
            <div className="image-previews">
              {images.map((file, idx) => (
                <div
                  className="image-preview"
                  key={idx}
                  style={{ marginBottom: '10px', display: 'flex', alignItems: 'center' }}
                >
                  <img
                    src={URL.createObjectURL(file)}
                    alt={file.name}
                    style={{
                      width: '90px',
                      height: '80px',
                      objectFit: 'cover',
                      borderRadius: '5px',
                      marginRight: '12px'
                    }}
                  />
                  <div>
                    <div><strong>{file.name}</strong></div>
                    <div>Size: {(file.size / 1024).toFixed(1)} KB</div>
                  </div>
                  <button
                    type="button"
                    onClick={() => removeImage(idx)}
                    style={{
                      marginLeft: '14px',
                      background: '#db4437',
                      color: '#fff',
                      border: 'none',
                      borderRadius: '4px',
                      padding: '4px 10px',
                      cursor: 'pointer'
                    }}
                  >Remove</button>
                </div>
              ))}
            </div>
          )}
        </div>
        <button type="submit" disabled={loading || !isReadyToSubmit}>
          {loading ? 'Submitting...' : 'Submit Grievance'}
        </button>
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </form>
    </div>
  );
};

export default GrievanceFormContent;
