// In src/context/AuthContext.js
import React, { createContext, useState, useEffect } from 'react';


export const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
    // Store an object containing both token and is_staff status
    const [auth, setAuth] = useState(() => {
        const token = localStorage.getItem('token');
        const isStaff = localStorage.getItem('is_staff') === 'true';
        if (token) {
            return { token, isStaff };
        }
        return null;
    });

    const { user } = useContext(AuthContext);
if (user?.is_staff) return null;

    // This effect keeps localStorage in sync
    useEffect(() => {
        if (auth) {
            localStorage.setItem('token', auth.token);
            localStorage.setItem('is_staff', auth.isStaff);
        } else {
            localStorage.removeItem('token');
            localStorage.removeItem('is_staff');
        }
    }, [auth]);

    // The login function now accepts the full data object from the API
 const login = (data) => {
    setAuth({ token: data.token, isStaff: data.user.is_staff });
};


    const logout = () => {
        setAuth(null);
    };

    // Provide the values to the rest of the app
    const value = {
        authToken: auth?.token,
        isStaff: auth?.isStaff,
        isAuthenticated: !!auth?.token,
        login,
        logout,
    };

    return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
