// In src/App.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import AdminPage from './pages/AdminPage'; // <-- Import AdminPage
import './App.css';

function App() {
  // Initialize state from localStorage
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [isStaff, setIsStaff] = useState(localStorage.getItem('is_staff') === 'true');

  const handleLogin = (data) => {
    const { key, is_staff } = data;
    setToken(key);
    setIsStaff(is_staff);
    localStorage.setItem('token', key);
    localStorage.setItem('is_staff', is_staff);
  };

  const handleLogout = () => {
    setToken(null);
    setIsStaff(false);
    localStorage.removeItem('token');
    localStorage.removeItem('is_staff');
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <nav style={{ marginBottom: '20px', fontSize: '1.2rem' }}>
            {token ? (
              <>
                <Link to="/" style={{ marginRight: '15px' }}>Home</Link>

                {/* vvv CONDITIONAL ADMIN LINK vvv */}
                {isStaff && (
                  <Link to="/admin" style={{ marginRight: '15px', color: 'cyan' }}>Admin</Link>
                )}

                <Link to="/login" onClick={handleLogout}>Logout</Link>
              </>
            ) : (
              <>
                <Link to="/login" style={{ marginRight: '15px' }}>Login</Link>
                <Link to="/register">Register</Link>
              </>
            )}
          </nav>
          <Routes>
            <Route path="/" element={<HomePage token={token} />} />
            <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
            <Route path="/register" element={<RegisterPage />} />

            {/* vvv PROTECTED ADMIN ROUTE vvv */}
            <Route 
              path="/admin" 
              element={isStaff ? <AdminPage /> : <Navigate to="/" />} 
            />
          </Routes>
        </header>
      </div>
    </Router>
  );
}

export default App;
