// In src/pages/LoginPage.js

import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // <-- FIX: 'axios' is now imported
import { AuthContext } from '../context/AuthContext'; // This import is correct

const LoginPage = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    // --- FIX: Get the 'login' function from the context ---
    const { login } = useContext(AuthContext);
    // ----------------------------------------------------

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null); // Clear previous errors
        try {
            const response = await axios.post('http://127.0.0.1:8000/api/auth/login/', {
                username,
                password,
            });
            // Call the login function from the context with the user data
            login(response.data);
            
            // Redirect based on the role
            if (response.data.is_staff) {
                navigate('/admin');
            } else {
                navigate('/');
            }
        } catch (err) {
            setError('Invalid credentials. Please try again.');
            console.error("Login error:", err); // Log the full error for debugging
        }
    };

    return (
        <div>
            <h2>Login</h2>
            {/* --- FIX: The onSubmit handler now correctly points to handleSubmit --- */}
            <form onSubmit={handleSubmit}>
                <div><input type="text" value={username} onChange={e => setUsername(e.target.value)} placeholder="Username" required /></div>
                <div><input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" required /></div>
                <button type="submit">Login</button>
            </form>
            {/* Display error messages to the user */}
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default LoginPage;
