import React, { useEffect, useState } from 'react';
import apiClient from '../api';

function TriageDashboard() {
  const [grievances, setGrievances] = useState([]);
  const [categories, setCategories] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [assignLoading, setAssignLoading] = useState({});
  const [assignSuccess, setAssignSuccess] = useState({});
  const [assignError, setAssignError] = useState({});
  const [categoryAssign, setCategoryAssign] = useState({});
  const [departmentAssign, setDepartmentAssign] = useState({});

  // Recursively flatten all leaf categories (for selection)
  function getLeafCategories(categories) {
    if (!categories) return [];
    let leaves = [];
    categories.forEach(cat => {
      if (cat.subcategories && cat.subcategories.length > 0) {
        leaves = leaves.concat(getLeafCategories(cat.subcategories));
      } else {
        leaves.push(cat);
      }
    });
    return leaves;
  }

  // Fetch triage grievances
  const fetchGrievances = () => {
    apiClient.get('triage/grievances/')
      .then(res => {
        setGrievances(res.data);
      })
      .catch(err => console.error("Error fetching grievances:", err));
  };

  // Fetch categories
  const fetchCategories = () => {
    apiClient.get('categories/')
      .then(res => {
        setCategories(res.data || []);
      })
      .catch(err => console.error("Error fetching categories:", err));
  };

  // Fetch departments
  const fetchDepartments = () => {
    apiClient.get('departments/')
      .then(res => {
        setDepartments(res.data || []);
      })
      .catch(err => console.error("Error fetching departments:", err));
  };

  useEffect(() => {
    fetchGrievances();
    fetchCategories();
    fetchDepartments();
  }, []);

  // LEAF complaint categories only
  const leafCategories = getLeafCategories(categories).filter(c => c.name !== 'Other' && c.name !== 'In Review');

  // Filter out TRIAGE department for assignment
  const assignableDepartments = departments.filter(d => d.name !== 'Grievance Triage');

  // When assigning, department can be set according to the selected category's auto-mapping (in backend)
  const handleAssign = (grievanceId, categoryId, departmentId) => {
    setAssignLoading({ ...assignLoading, [grievanceId]: true });
    apiClient.post(`triage/grievances/${grievanceId}/assign/`, { category_id: categoryId, department_id: departmentId })
      .then(() => {
        setAssignSuccess({ ...assignSuccess, [grievanceId]: true });
        setAssignLoading({ ...assignLoading, [grievanceId]: false });
        fetchGrievances();  // Refresh list after assign
      })
      .catch(() => {
        setAssignError({ ...assignError, [grievanceId]: 'Error updating grievance.' });
        setAssignLoading({ ...assignLoading, [grievanceId]: false });
      });
  };

  return (
    <div>
      <h2>Triage Dashboard</h2>
      {grievances.length === 0 ? (
        <p>No grievances pending triage.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>ID</th><th>Description</th><th>Submitter</th><th>Date</th>
              <th>New Category</th><th>New Department</th><th>Assign</th>
            </tr>
          </thead>
          <tbody>
            {grievances.map(g => (
              <tr key={g.id}>
                <td>{g.id}</td>
                <td>{g.description}</td>
                <td>{g.user_name}</td>
                <td>{g.submitted_at}</td>
                <td>
                  <select
                    value={categoryAssign[g.id] || ""}
                    onChange={e => setCategoryAssign({ ...categoryAssign, [g.id]: e.target.value })}
                  >
                    <option value="" disabled>Select category</option>
                    {leafCategories.map(c => (
                      <option key={c.id} value={c.id}>
                        {c.full_path || c.name}
                      </option>
                    ))}
                  </select>
                </td>
                <td>
                  <select
                    value={departmentAssign[g.id] || ""}
                    onChange={e => setDepartmentAssign({ ...departmentAssign, [g.id]: e.target.value })}
                  >
                    <option value="" disabled>Select department</option>
                    {assignableDepartments.map(d => (
                      <option key={d.id} value={d.id}>{d.name}</option>
                    ))}
                  </select>
                </td>
                <td>
                  <button
                    onClick={() => handleAssign(g.id, categoryAssign[g.id], departmentAssign[g.id])}
                    disabled={assignLoading[g.id] || !categoryAssign[g.id] || !departmentAssign[g.id]}
                  >
                    {assignLoading[g.id] ? 'Assigning...' : 'Assign'}
                  </button>
                  {assignSuccess[g.id] && <span style={{ color: 'green' }}>Assigned!</span>}
                  {assignError[g.id] && <span style={{ color: 'red' }}>{assignError[g.id]}</span>}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default TriageDashboard;
