// In src/pages/AdminPage.js

import React, { useState, useEffect } from 'react';

function AdminPage() {
    const [grievances, setGrievances] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchGrievances = async () => {
            const token = localStorage.getItem('token');

            if (!token) {
                setError('Authentication token not found. Please log in again.');
                setLoading(false);
                return;
            }

            try {
                const response = await fetch('/api/admin/grievances/', {
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(errorData.detail || 'Failed to fetch grievances.');
                }

                const data = await response.json();
                setGrievances(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchGrievances();
    }, []); // The empty array means this effect runs once when the component loads

    if (loading) {
        return <div style={{ color: 'white' }}>Loading grievances...</div>;
    }

    if (error) {
        return <div style={{ color: 'red' }}>Error: {error}</div>;
    }

    return (
        <div>
            <h2 style={{ color: 'white' }}>Admin Dashboard</h2>
            <table border="1" style={{ width: '100%', borderCollapse: 'collapse', color: 'white' }}>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Status</th>
                        <th>Submitted By</th>
                    </tr>
                </thead>
                <tbody>
                    {grievances.map(grievance => (
                        <tr key={grievance.id}>
                            <td>{grievance.id}</td>
                            <td>{grievance.title}</td>
                            <td>{grievance.status}</td>
                            <td>{grievance.user}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default AdminPage;
