// In src/components/GrievanceForm.js

import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const GrievanceForm = () => {
    const { authToken } = useContext(AuthContext);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [department, setDepartment] = useState('');
    const [category, setCategory] = useState('');
    const [otherCategoryText, setOtherCategoryText] = useState('');
    const [departments, setDepartments] = useState([]);
    const [categories, setCategories] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const deptResponse = await axios.get('http://127.0.0.1:8000/api/departments/');
                setDepartments(deptResponse.data || []);
                const catResponse = await axios.get('http://127.0.0.1:8000/api/categories/');
                setCategories(catResponse.data || []);
            } catch (err) {
                setError('Failed to load form data. Please refresh the page.');
                setDepartments([]);
                setCategories([]);
            }
        };
        fetchData();
    }, []);

    useEffect(() => {
        if (category === 'other') {
            const triageDept = departments.find(dept => dept.name === 'Grievance Triage');
            if (triageDept) {
                setDepartment(triageDept.id);
            }
        }
    }, [category, departments]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setLoading(true);

        const grievanceData = {
            title,
            description,
            department: department || null,
            category: category === 'other' ? null : (category || null),
            other_category: category === 'other' ? otherCategoryText : null,
        };

        try {
            await axios.post('http://127.0.0.1:8000/api/grievances/', grievanceData, {
                headers: { 'Authorization': `Token ${authToken}` }
            });
            navigate('/');
        } catch (err) {
            if (err.response && err.response.data) {
                const errorMessages = Object.entries(err.response.data)
                    .map(([field, messages]) => `${field}: ${messages.join(' ')}`)
                    .join(' | ');
                setError(errorMessages);
            } else {
                setError('An unexpected error occurred. Please try again.');
            }
        } finally {
            setLoading(false);
        }
    };
    
    const getSelectableCategories = () => {
        if (!categories || categories.length === 0) return [];
        const parentIds = new Set(categories.map(cat => cat.parent).filter(id => id !== null));
        return categories.filter(cat => !parentIds.has(cat.id));
    };

    return (
        <div className="form-container">
            <h2>Submit a New Grievance</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="title">Title</label>
                    <input type="text" id="title" value={title} onChange={(e) => setTitle(e.target.value)} required />
                </div>
                <div className="form-group">
                    <label htmlFor="description">Description</label>
                    <textarea id="description" value={description} onChange={(e) => setDescription(e.target.value)} required />
                </div>
                <div className="form-group">
                    <label htmlFor="category">Select your complaint</label>
                    <select id="category" value={category} onChange={(e) => setCategory(e.target.value)} required>
                        <option value="">Select a complaint type...</option>
                        {(getSelectableCategories() || []).map(cat => <option key={cat.id} value={cat.id}>{cat.name}</option>)}
                        <option value="other">Other (Please specify)</option>
                    </select>
                </div>

                {/* This is the corrected conditional block for the "Other" text input */}
                {category === 'other' && (
                    <div className="form-group">
                        <label htmlFor="other-category">Please specify your complaint type</label>
                        <input
                            type="text"
                            id="other-category"
                            value={otherCategoryText}
                            onChange={(e) => setOtherCategoryText(e.target.value)}
                            placeholder="e.g., 'Broken park bench' or 'Stray animal issue'"
                            required 
                        />
                    </div>
                )}
                
                <div className="form-group">
                    <label htmlFor="department">Department</label>
                    <select 
                        id="department" 
                        value={department} 
                        onChange={(e) => setDepartment(e.target.value)} 
                        required 
                        disabled={category === 'other'}
                    >
                        <option value="">Select a Department</option>
                        {(departments || []).map(dept => <option key={dept.id} value={dept.id}>{dept.name}</option>)}
                    </select>
                </div>
                <button type="submit" disabled={loading}>{loading ? 'Submitting...' : 'Submit Grievance'}</button>
                {error && <p style={{color: 'red'}}>{error}</p>}
            </form>
        </div>
    );
};

export default GrievanceForm;
