import React, { useState, useEffect } from 'react';
import GrievanceAdminEdit from './GrievanceAdminEdit';

function AdminDashboard() {
  // ... [your current code for fetching and listing grievances]
  const [editingId, setEditingId] = useState(null);
  const token = localStorage.getItem('token');

  const handleEditSuccess = () => {
    setEditingId(null);
    // Refresh grievances list
    fetchGrievances();
  };

  if (editingId) {
    return (
      <div>
        <button onClick={() => setEditingId(null)}>Back to Dashboard</button>
        <GrievanceAdminEdit
          grievanceId={editingId}
          authToken={token}
          onUpdateSuccess={handleEditSuccess}
        />
      </div>
    );
  }

  // ... [render your table, adding "Edit" button:]
  return (
    <table>
      {/* header */}
      <tbody>
        {grievances.map(g => (
          <tr key={g.id}>
            {/* other columns */}
            <td>
              <button onClick={() => setEditingId(g.id)}>
                Edit
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
