import React, { useEffect, useState } from 'react';

function TriageDashboard() {
  const [grievances, setGrievances] = useState([]);
  const [categories, setCategories] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [assignLoading, setAssignLoading] = useState({});
  const [assignSuccess, setAssignSuccess] = useState({});
  const [assignError, setAssignError] = useState({});

  useEffect(() => {
    // Fetch grievances for triage
    fetch('/api/triage/grievances/')
      .then((res) => res.json())
      .then(setGrievances);

    // Fetch all valid categories/departments (for dropdowns)
    fetch('/api/categories/').then((res) => res.json()).then(setCategories);
    fetch('/api/departments/').then((res) => res.json()).then(setDepartments);
  }, []);

  // Assign grievance handler
  const handleAssign = (grievanceId, categoryId, departmentId) => {
    setAssignLoading({ ...assignLoading, [grievanceId]: true });
    fetch(`/api/triage/grievances/${grievanceId}/assign/`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ category_id: categoryId, department_id: departmentId }),
    })
      .then((res) => res.json())
      .then((data) => {
        setAssignSuccess({ ...assignSuccess, [grievanceId]: true });
        setAssignLoading({ ...assignLoading, [grievanceId]: false });
        // Optionally, remove reassigned grievance from the table
        setGrievances(grievances.filter(g => g.id !== grievanceId));
      })
      .catch((error) => {
        setAssignError({ ...assignError, [grievanceId]: 'Error updating grievance.' });
        setAssignLoading({ ...assignLoading, [grievanceId]: false });
      });
  };

  return (
    <div>
      <h2>Triage Dashboard</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Description</th>
            <th>Submitter</th>
            <th>Date</th>
            <th>New Category</th>
            <th>New Department</th>
            <th>Assign</th>
          </tr>
        </thead>
        <tbody>
          {grievances.map(grievance => (
            <tr key={grievance.id}>
              <td>{grievance.id}</td>
              <td>{grievance.description}</td>
              <td>{grievance.user_name}</td>
              <td>{grievance.submitted_at}</td>
              <td>
                <select
                  defaultValue=""
                  onChange={e => grievance.newCategoryId = e.target.value}
                >
                  <option value="" disabled>Select category</option>
                  {categories
                    .filter(cat => cat.name !== 'Other') // Exclude 'Other'
                    .map(cat => (
                      <option key={cat.id} value={cat.id}>{cat.name}</option>
                    ))}
                </select>
              </td>
              <td>
                <select
                  defaultValue=""
                  onChange={e => grievance.newDepartmentId = e.target.value}
                >
                  <option value="" disabled>Select department</option>
                  {departments
                    .filter(dept => dept.name !== 'Grievance Triage') // Exclude triage
                    .map(dept => (
                      <option key={dept.id} value={dept.id}>{dept.name}</option>
                    ))}
                </select>
              </td>
              <td>
                <button
                  onClick={() => handleAssign(grievance.id, grievance.newCategoryId, grievance.newDepartmentId)}
                  disabled={assignLoading[grievance.id]}
                >
                  {assignLoading[grievance.id] ? 'Assigning...' : 'Assign'}
                </button>
                {assignSuccess[grievance.id] && <span style={{ color: 'green' }}>Assigned!</span>}
                {assignError[grievance.id] && <span style={{ color: 'red' }}>{assignError[grievance.id]}</span>}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TriageDashboard;
