// In src/components/GrievanceForm.js

import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const GrievanceForm = () => {
    const { authToken } = useContext(AuthContext);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [department, setDepartment] = useState('');
    const [category, setCategory] = useState('');
    
    // --- NEW STATE for the "Other" text field ---
    const [otherCategoryText, setOtherCategoryText] = useState('');

    const [departments, setDepartments] = useState([]);
    const [categories, setCategories] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        // ... (this useEffect hook is unchanged)
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setLoading(true);

        // --- UPDATED LOGIC for handling "Other" ---
        const grievanceData = {
            title,
            description,
            department: department || null,
            // If category is 'other', send null for the category ID
            category: category === 'other' ? null : (category || null),
            // If category is 'other', send the text from the new input field
            other_category: category === 'other' ? otherCategoryText : null,
        };
        // ------------------------------------------

        try {
            await axios.post('http://127.0.0.1:8000/api/grievances/', grievanceData, {
                headers: { 'Authorization': `Token ${authToken}` }
            });
            navigate('/');
        } catch (err) {
            // ... (error handling is unchanged)
        } finally {
            setLoading(false);
        }
    };
    
    const getSelectableCategories = () => {
        // ... (this function is unchanged)
    };

    return (
        <div className="form-container">
            <h2>Submit a New Grievance</h2>
            <form onSubmit={handleSubmit}>
                {/* Title and Description are unchanged */}
                
                <div className="form-group">
                    <label htmlFor="category">Select your complaint</label>
                    <select id="category" value={category} onChange={(e) => setCategory(e.target.value)} required>
                        <option value="">Select a complaint type...</option>
                        {getSelectableCategories().map(cat => <option key={cat.id} value={cat.id}>{cat.name}</option>)}
                        {/* --- NEW "Other" option --- */}
                        <option value="other">Other (Please specify)</option>
                    </select>
                </div>

                {/* --- NEW Conditional text field --- */}
                {/* This input only appears if the user selects "Other" */}
                {category === 'other' && (
                    <div className="form-group">
                        <label htmlFor="other-category">Please specify your complaint type</label>
                        <input
                            type="text"
                            id="other-category"
                            value={otherCategoryText}
                            onChange={(e) => setOtherCategoryText(e.target.value)}
                            placeholder="e.g., 'Broken park bench' or 'Stray animal issue'"
                            required 
                        />
                    </div>
                )}
                
                <div className="form-group">
                    <label htmlFor="department">Department</label>
                    <select id="department" value={department} onChange={(e) => setDepartment(e.target.value)} required>
                        <option value="">Select a Department</option>
                        {departments.map(dept => <option key={dept.id} value={dept.id}>{dept.name}</option>)}
                    </select>
                </div>

                <button type="submit" disabled={loading}>{loading ? 'Submitting...' : 'Submit Grievance'}</button>
                {error && <p style={{color: 'red'}}>{error}</p>}
            </form>
        </div>
    );
};

export default GrievanceForm;
