import React, { useState, useEffect } from 'react';
import apiClient from '../apiClient';

const HealthDashboard = () => {
  const [grievances, setGrievances] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGrievances = async () => {
      try {
        setLoading(true);
        setError(null);
        const response = await apiClient.get('grievances/', {
          params: { department: 'Health (Public Health)' }
        });
        setGrievances(response.data);
      } catch (err) {
        setError("Could not load grievances. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchGrievances();
  }, []);

  if (loading) return <h2>Loading grievances...</h2>;
  if (error) return <h2 style={{ color: 'red' }}>{error}</h2>;

  return (
    <div>
      <h1>Health Department Grievances</h1>
      {grievances.map(grievance => (
        <div key={grievance.id}>
          <h3>{grievance.title}</h3>
          <p>{grievance.description}</p>
        </div>
      ))}
    </div>
  );
};

export default HealthDashboard;
