import React, { createContext, useState, useEffect } from 'react';

export const AuthContext = createContext(null);
const userGroups = auth?.user?.groups || [];


export const AuthProvider = ({ children }) => {
  // Store both token and user object in context (persistent on reload)
  const [auth, setAuth] = useState(() => {
    const token = localStorage.getItem('token');
    const userJson = localStorage.getItem('user');
    const user = userJson ? JSON.parse(userJson) : null;
    
    if (token && user) {
      return { token, user };
    }
    return null;
  });

  // Sync localStorage whenever auth changes
  useEffect(() => {
    if (auth && auth.token && auth.user) {
      localStorage.setItem('token', auth.token);
      localStorage.setItem('user', JSON.stringify(auth.user));
    } else {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
    }
  }, [auth]);

  // The login API response must include { token, user: {...} }
  const login = (data) => {
    setAuth({ token: data.token, user: data.user });
  };

  const logout = () => {
    setAuth(null);
  };

  return (
    <AuthContext.Provider value={{
      authToken: auth?.token || null,
      user: auth?.user || null,
      userGroups,
      isStaff: auth?.user?.is_staff === true,
      isAuthenticated: !!auth?.token,
      login,
      logout,
    }}>
      {children}
    </AuthContext.Provider>
  );
};
