import React, { useEffect, useState } from 'react';
import apiClient from '../api'; // Adjust path as needed

function TriageDashboard() {
  const [grievances, setGrievances] = useState([]);
  const [categories, setCategories] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [assignLoading, setAssignLoading] = useState({});
  const [assignSuccess, setAssignSuccess] = useState({});
  const [assignError, setAssignError] = useState({});
  const [categoryAssign, setCategoryAssign] = useState({});
const [departmentAssign, setDepartmentAssign] = useState({});

useEffect(() => {
  // Fetch triage grievances (authenticated)
  apiClient.get('triage/grievances/')
    .then(res => setGrievances(res.data))
    .catch(err => console.error(err));

  // Fetch all categories (authenticated)
  apiClient.get('categories/')
    .then(res => setCategories(res.data))
    .catch(err => console.error(err));

  // Fetch all departments (authenticated)
  apiClient.get('departments/')
    .then(res => setDepartments(res.data))
    .catch(err => console.error(err));
}, []);



  // Assign grievance handler
  const handleAssign = (grievanceId, categoryId, departmentId) => {
    setAssignLoading({ ...assignLoading, [grievanceId]: true });
    fetch(`/api/triage/grievances/${grievanceId}/assign/`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ category_id: categoryId, department_id: departmentId }),
    })
      .then((res) => res.json())
      .then((data) => {
        setAssignSuccess({ ...assignSuccess, [grievanceId]: true });
        setAssignLoading({ ...assignLoading, [grievanceId]: false });
        // Optionally, remove reassigned grievance from the table
        setGrievances(grievances.filter(g => g.id !== grievanceId));
      })
      .catch((error) => {
        setAssignError({ ...assignError, [grievanceId]: 'Error updating grievance.' });
        setAssignLoading({ ...assignLoading, [grievanceId]: false });
      });
  };

  return (
    <div>
      <h2>Triage Dashboard</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Description</th>
            <th>Submitter</th>
            <th>Date</th>
            <th>New Category</th>
            <th>New Department</th>
            <th>Assign</th>
          </tr>
        </thead>
        <tbody>
          {grievances.map(grievance => (
            <tr key={grievance.id}>
              <td>{grievance.id}</td>
              <td>{grievance.description}</td>
              <td>{grievance.user_name}</td>
              <td>{grievance.submitted_at}</td>
              <td>
                <select
  value={categoryAssign[grievance.id] || ""}
  onChange={e =>
    setCategoryAssign({ ...categoryAssign, [grievance.id]: e.target.value })
  }
>
  <option value="" disabled>Select category</option>
  {categories
    .filter(cat => cat.name !== 'Other' && cat.name !== 'In Review') // Exclude both
    .map(cat => (
      <option key={cat.id} value={cat.id}>{cat.name}</option>
    ))}
</select>
              </td>
              <td>
                <select
  value={departmentAssign[grievance.id] || ""}
  onChange={e =>
    setDepartmentAssign({ ...departmentAssign, [grievance.id]: e.target.value })
  }
>
  <option value="" disabled>Select department</option>
  {departments
    .filter(dept => dept.name !== 'Grievance Triage') // Exclude triage dept
    .map(dept => (
      <option key={dept.id} value={dept.id}>{dept.name}</option>
    ))}
</select>
              </td>
              <td>
                <button
  onClick={() => handleAssign(
    grievance.id,
    categoryAssign[grievance.id],
    departmentAssign[grievance.id]
  )}
  disabled={assignLoading[grievance.id]
    || !categoryAssign[grievance.id]
    || !departmentAssign[grievance.id]}
>
  {assignLoading[grievance.id] ? 'Assigning...' : 'Assign'}
</button>
                {assignSuccess[grievance.id] && <span style={{ color: 'green' }}>Assigned!</span>}
                {assignError[grievance.id] && <span style={{ color: 'red' }}>{assignError[grievance.id]}</span>}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TriageDashboard;
