import React, { useState, useEffect, useContext } from 'react';
import AdminGrievanceEditor from './AdminGrievanceEditor';
import { AuthContext } from '../context/AuthContext';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function AdminDashboard() {
  const { authToken } = useContext(AuthContext); // use context only
  const [grievances, setGrievances] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const navigate = useNavigate();

  // Redirect immediately if not authenticated
  useEffect(() => {
    if (!authToken) {
      navigate('/login', { replace: true });
    }
  }, [authToken, navigate]);

  const fetchGrievances = async () => {
  try {
    setLoading(true);
    const res = await axios.get(
      'http://127.0.0.1:8000/api/admin/grievances/',
      { headers: { Authorization: `Token ${authToken}` } }
    );
    // FILTER OUT TRIAGE CASES:
    const filtered = res.data.filter(
      g =>
        g.category_name !== 'In Review' ||
        g.department_name !== 'Grievance Triage'
    );
    setGrievances(filtered);
    setError(null);
  } catch (error) {
    if (
      error.response &&
      (error.response.status === 401 || error.response.status === 403)
    ) {
      navigate('/login', { replace: true });
      return;
    }
    setError('Failed to load grievances.');
  } finally {
    setLoading(false);
  }
};

  useEffect(() => {
    if (authToken) {
      fetchGrievances();
    }
    // eslint-disable-next-line
  }, [authToken]);

  const handleUpdateSuccess = () => {
    setEditingId(null);
    fetchGrievances();
  };

  if (!authToken) return null; // Prevent render if not authenticated

  if (loading) return <p>Loading grievances...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  if (editingId)
    return (
      <div>
        <button onClick={() => setEditingId(null)}>Back to Dashboard</button>
        <AdminGrievanceEditor
          grievanceId={editingId}
          onUpdateSuccess={handleUpdateSuccess}
        />
      </div>
    );

  return (
    <div>
      <h2>Admin Dashboard - Grievances</h2>
      <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
  <tr>
    <th>Title</th>
    <th>Category</th>
    <th>Status</th>
    <th>Edit</th>
  </tr>
</thead>
<tbody>
  {grievances.map(g => (
    <tr key={g.id}>
      <td>{g.title}</td>
      <td>
        {/* Try both, depending on backend JSON. Prefer category_name. */}
        {g.category_name || (g.category && g.category.name) || g.category || 'N/A'}
      </td>
      <td>{g.status}</td>
      <td>
        <button onClick={() => setEditingId(g.id)}>Edit</button>
      </td>
    </tr>
  ))}
</tbody>
      </table>
    </div>
  );
}
