// In src/pages/RegisterPage.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api';

function RegisterPage() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [passwordConfirm, setPasswordConfirm] = useState('');
  const navigate = useNavigate();

  const handleRegister = (event) => {
    event.preventDefault();
    console.log("Attempting to register with username:", username); // Debug log

    const registrationData = {
      username,
      email,
      password,
      password1: passwordConfirm,
      password2: passwordConfirm,
    };

    api.post('auth/registration/', registrationData)
    .then(response => {
      // --- THIS IS THE SUCCESS BLOCK ---
      console.log('API call was successful. Response:', response);
      alert('Registration Successful! Please log in.');
      console.log('Navigating to /login...');
      navigate('/login');
      // --- END OF SUCCESS BLOCK ---
    })
    .catch(error => {
      // --- THIS IS THE FAILURE BLOCK ---
      console.error('API call failed. The error is:', error);
      if (error.response) {
        console.error('The backend responded with this error data:', error.response.data);
        alert('Registration failed: ' + JSON.stringify(error.response.data));
      } else {
        alert('Registration failed: A network error or another issue occurred.');
      }
      // --- END OF FAILURE BLOCK ---
    });
  };

  return (
    <div>
      <h2>Register</h2>
      <form onSubmit={handleRegister}>
        <div><input type="text" value={username} onChange={e => setUsername(e.target.value)} placeholder="Username" required /></div>
        <div><input type="email" value={email} onChange={e => setEmail(e.target.value)} placeholder="Email" required /></div>
        <div><input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" required /></div>
        <div><input type="password" value={passwordConfirm} onChange={e => setPasswordConfirm(e.target.value)} placeholder="Confirm Password" required /></div>
        <button type="submit">Register</button>
      </form>
    </div>
  );
}

export default RegisterPage;
