// In src/components/GrievanceForm.js

import React, { useState } from 'react';
import api from '../api'; // <-- IMPORT OUR API CLIENT

function GrievanceForm({ onGrievanceCreated }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    // Use 'api' instead of 'axios' and the shortened URL
    api.post('grievances/', {
      title: title,
      description: description,
    })
    .then(response => {
      console.log('Grievance created:', response.data);
      setTitle('');
      setDescription('');
      onGrievanceCreated();
    })
    .catch(error => {
      console.error("There was an error creating the grievance!", error);
    });
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginTop: '20px' }}>
      <h3>Submit a New Grievance</h3>
      <div>
        <label>Title: </label>
        <input
          type="text"
          value={title}
          onChange={e => setTitle(e.target.value)}
          required
        />
      </div>
      <div style={{ marginTop: '10px' }}>
        <label>Description: </label>
        <textarea
          value={description}
          onChange={e => setDescription(e.target.value)}
          required
        />
      </div>
      <button type="submit" style={{ marginTop: '10px' }}>Submit</button>
    </form>
  );
}

export default GrievanceForm;
