// In src/App.js

import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

// Import all the page components
import Home from './components/Home';
import Login from './components/Login';
import Register from './components/Register';
import Dashboard from './components/Dashboard';
import GrievanceForm from './components/GrievanceForm';

// --- NEW: Import the AdminDashboard component ---
import AdminDashboard from './components/AdminDashboard';

function App() {
  const isLoggedIn = !!localStorage.getItem('token');

  const handleLogout = () => {
    localStorage.removeItem('token');
    // Redirect to login page after logout
    window.location.href = '/login';
  };

  return (
    <Router>
      <div>
        <nav>
          {/* Your navigation bar */}
          <ul>
            <li><Link to="/">Home</Link></li>
            {isLoggedIn ? (
              <>
                <li><Link to="/dashboard">My Dashboard</Link></li>
                <li><Link to="/submit-grievance">Submit Grievance</Link></li>
                {/* We will add an admin link later, for now, navigate manually */}
                <li><button onClick={handleLogout}>Logout</button></li>
              </>
            ) : (
              <>
                <li><Link to="/login">Login</Link></li>
                <li><Link to="/register">Register</Link></li>
              </>
            )}
          </ul>
        </nav>

        <hr />

        {/* --- All your application routes --- */}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/submit-grievance" element={<GrievanceForm />} />

          {/* --- NEW: The route for the admin dashboard --- */}
          <Route path="/admin-dashboard" element={<AdminDashboard />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
