import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import apiClient from '../apiClient'; // your axios instance with auth headers

export default function AdminGrievanceEditor({ grievanceId, onUpdateSuccess }) {
  const { authToken } = useContext(AuthContext);
  const [status, setStatus] = useState('');
  const [resolutionNotes, setResolutionNotes] = useState('');
  const [signedDocument, setSignedDocument] = useState(null);
  const [resolutionImage, setResolutionImage] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [submitLoading, setSubmitLoading] = useState(false);
  const [events, setEvents] = useState([]);
 

  // Fetch grievance details and event log on component mount or grievanceId change
  useEffect(() => {
    const fetchGrievance = async () => {
      try {
        setLoading(true);
        const response = await apiClient.get(`grievances/${grievanceId}/`);
        setStatus(response.data.status);
        setResolutionNotes(response.data.resolution_notes || '');
        setError(null);
      } catch {
        setError('Failed to load grievance details.');
      } finally {
        setLoading(false);
      }
    };

    const fetchEvents = async () => {
      try {
        const res = await apiClient.get(`grievances/${grievanceId}/events/`);
        setEvents(res.data);
      } catch {
        // Optional: handle events load error
      }
    };

    fetchGrievance();
    fetchEvents();
  }, [grievanceId, authToken]);

  // Handle grievance update submit with files
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSubmitLoading(true);
    const formData = new FormData();
    formData.append('status', status);
    formData.append('resolution_notes', resolutionNotes);
    if (signedDocument) formData.append('signed_document', signedDocument);
    if (resolutionImage) formData.append('resolution_image', resolutionImage);

    try {
      await apiClient.patch(`grievances/${grievanceId}/`, formData);
      onUpdateSuccess();

      // Refresh event log after update
      const res = await apiClient.get(`grievances/${grievanceId}/events/`);
      setEvents(res.data);
    } catch {
      setError('Failed to update grievance.');
    } finally {
      setSubmitLoading(false);
    }
  };

  // Handle reopen grievance with required reason
  const handleReopen = async () => {
    if (!reopenReason.trim()) {
      setReopenError('Please provide a reason for reopening.');
      return;
    }
    setReopenError(null);
    setReopenLoading(true);
    try {
      await apiClient.post(`grievances/${grievanceId}/reopen/`, { reason: reopenReason });
      setReopenReason('');
      onUpdateSuccess();

      // Refresh grievance and event log after reopen
      const response = await apiClient.get(`grievances/${grievanceId}/`);
      setStatus(response.data.status);
      setResolutionNotes(response.data.resolution_notes || '');

      const res = await apiClient.get(`grievances/${grievanceId}/events/`);
      setEvents(res.data);
    } catch {
      setReopenError('Failed to reopen grievance.');
    } finally {
      setReopenLoading(false);
    }
  };

  if (loading) return <p>Loading grievance details...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 600 }}>
      <h2>Edit Grievance</h2>

      <div>
        <label htmlFor="status">Status:</label><br />
        <select id="status" value={status} onChange={(e) => setStatus(e.target.value)} required>
          <option value="Pending">Pending</option>
          <option value="In Progress">In Progress</option>
          <option value="Resolved">Resolved</option>
          <option value="Rejected">Rejected</option>
          <option value="Closed">Closed</option>
        </select>
      </div>

      <div>
        <label htmlFor="resolutionNotes">Resolution Notes:</label><br />
        <textarea id="resolutionNotes" value={resolutionNotes} onChange={(e) => setResolutionNotes(e.target.value)} rows="5" style={{ width: '100%' }}/>
      </div>

      <div>
        <label htmlFor="signedDocument">Upload Signed Document:</label><br />
        <input id="signedDocument" type="file" accept=".pdf,.doc,.docx" onChange={(e) => setSignedDocument(e.target.files[0])} />
      </div>

      <div>
        <label htmlFor="resolutionImage">Upload Resolution Image:</label><br />
        <input id="resolutionImage" type="file" accept="image/*" onChange={(e) => setResolutionImage(e.target.files[0])} />
      </div>

      
      <h3>Event Log / History</h3>
      <ul>
        {events.length === 0 && <li>No audit events yet.</li>}
        {events.map((ev) => (
          <li key={ev.id}>
            [{new Date(ev.timestamp).toLocaleString()}] {ev.action} by {(ev.user && (ev.user.username || ev.user)) || 'System'}
            {ev.notes && <span>: {ev.notes}</span>}
          </li>
        ))}
      </ul>

      <button type="submit" style={{ marginTop: 10 }} disabled={submitLoading}>
        {submitLoading ? 'Saving...' : 'Save'}
      </button>
    </form>
  );
}
