import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const TRIAGE_DEPT_NAME = 'Grievance Triage';

const GrievanceFormContent = () => {
  const { authToken } = useContext(AuthContext);
  const [description, setDescription] = useState('');
  const [department, setDepartment] = useState('');
  const [category, setCategory] = useState('');
  const [departments, setDepartments] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Find backend "Other" category once categories are loaded
  const otherCatObj = categories.find(cat => cat.name.toLowerCase() === 'other');
  const otherCatId = otherCatObj ? String(otherCatObj.id) : null;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const deptResponse = await axios.get('http://127.0.0.1:8000/api/departments/');
        setDepartments(deptResponse.data || []);
        const catResponse = await axios.get('http://127.0.0.1:8000/api/categories/');
        setCategories(catResponse.data || []);
      } catch (err) {
        setError('Failed to load form data. Please refresh the page.');
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    if (category === otherCatId) {
      const triageDept = departments.find(dept => dept.name === TRIAGE_DEPT_NAME);
      if (triageDept) {
        setDepartment(triageDept.id);
      }
    } else {
      setDepartment('');
    }
  }, [category, departments, otherCatId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('description', description);
    formData.append('department', department || '');
    formData.append('category', String(category));

    try {
      await axios.post(
        'http://127.0.0.1:8000/api/grievances/',
        formData,
        { headers: { 'Authorization': `Token ${authToken}` } }
      );
      navigate('/');
    } catch (err) {
      if (err.response && err.response.data) {
        const errorMessages = Object.entries(err.response.data)
          .map(([field, messages]) =>
            `${field}: ${Array.isArray(messages) ? messages.join(' ') : messages}`
          ).join(' | ');
        setError(errorMessages);
      } else {
        setError('An unexpected error occurred.');
      }
    } finally {
      setLoading(false);
    }
  };

  const getSelectableCategories = () => {
    if (!categories || categories.length === 0) return [];
    const parentIds = new Set(categories.map(cat => cat.parent).filter(id => id !== null));
    return categories.filter(cat => !parentIds.has(cat.id));
  };

  const selectableDepts = departments.filter(dept => dept.name !== TRIAGE_DEPT_NAME);
  const isReadyToSubmit = category ? !!department : false;

  return (
    <div className="form-container">
      <h2>Submit a New Grievance</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="description">Describe your complaint</label>
          <textarea id="description" value={description}
            onChange={e => setDescription(e.target.value)} required />
        </div>
        <div className="form-group">
          <label htmlFor="category">Select the complaint type</label>
          <select id="category" value={category} onChange={e => setCategory(e.target.value)} required>
            <option value="">Select a complaint type...</option>
            {getSelectableCategories().map(cat =>
              <option key={cat.id} value={cat.id}>{cat.name}</option>
            )}
          </select>
        </div>
        {/* Department selection only if not Other */}
        {category && category !== otherCatId && (
          <div className="form-group">
            <label htmlFor="department">Department</label>
            <select id="department" value={department} onChange={e => setDepartment(e.target.value)} required>
              <option value="">Select a Department</option>
              {selectableDepts.map(dept =>
                <option key={dept.id} value={dept.id}>{dept.name}</option>
              )}
            </select>
          </div>
        )}
        <button type="submit" disabled={loading || !isReadyToSubmit}>
          {loading ? 'Submitting...' : 'Submit Grievance'}
        </button>
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </form>
    </div>
  );
};

export default GrievanceFormContent;
