// In src/pages/AdminPage.js

import React, { useState, useEffect } from 'react';

function AdminPage() {
    const [grievances, setGrievances] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchGrievances = async () => {
            const token = localStorage.getItem('token');

            if (!token) {
                setError('Authentication token not found. Please log in again.');
                setLoading(false);
                return;
            }

            try {
                const response = await fetch('/api/admin/grievances/', {
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `to ${token}`
                    }
                });

                // If the server response is not "OK" (e.g., 403 Forbidden)
                if (!response.ok) {
                    const contentType = response.headers.get('content-type');
                    // Check if the server sent a JSON error or an HTML page
                    if (contentType && contentType.indexOf('application/json') !== -1) {
                        const errorData = await response.json();
                        throw new Error(errorData.detail || `Request failed: ${response.status}`);
                    } else {
                        // The error is an HTML page
                        throw new Error(`Permission Denied. The server responded with an HTML error page (status: ${response.status}). Please ensure you are logged in with an admin account.`);
                    }
                }

                const data = await response.json();
                setGrievances(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchGrievances();
    }, []);

    if (loading) {
        return <div style={{ color: 'white' }}>Loading grievances...</div>;
    }

    if (error) {
        return <div style={{ color: 'red' }}>Error: {error}</div>;
    }

    return (
        <div>
            <h2 style={{ color: 'white' }}>Admin Dashboard</h2>
            <table border="1" style={{ width: '100%', borderCollapse: 'collapse', color: 'white' }}>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Status</th>
                        <th>Submitted By</th>
                    </tr>
                </thead>
                <tbody>
                    {grievances.map(grievance => (
                        <tr key={grievance.id}>
                            <td>{grievance.id}</td>
                            <td>{grievance.title}</td>
                            <td>{grievance.status}</td>
                            {/* We will fix this to show the username later */}
                            <td>User ID: {grievance.user}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default AdminPage;
