import React, { useState } from 'react';


export default function ImageViewer({ image, width = 400, height = 300 }) {
  const [zoom, setZoom] = useState(1.0);
  const [pos, setPos] = useState({ x: 0, y: 0 });
  const [dragging, setDragging] = useState(false);
  const [start, setStart] = useState({ x: 0, y: 0 });

  // Mouse wheel for zoom in/out
  const handleWheel = (e) => {
    e.preventDefault();
    let delta = e.deltaY > 0 ? -0.1 : 0.1;
    setZoom(z => Math.max(0.5, Math.min(z + delta, 5)));
  };

  // Mouse down: start dragging
  const handleMouseDown = (e) => {
    setDragging(true);
    setStart({ x: e.clientX - pos.x, y: e.clientY - pos.y });
  };

  // Mouse move: update position if dragging
  const handleMouseMove = (e) => {
    if (dragging) {
      setPos({
        x: e.clientX - start.x,
        y: e.clientY - start.y,
      });
    }
  };

  // Mouse up: stop dragging
  const handleMouseUp = () => setDragging(false);

  // Reset zoom and pan
  const handleReset = () => {
    setZoom(1.0);
    setPos({ x: 0, y: 0 });
  };

  // Prevent dragging image itself
  const preventDragHandler = (e) => e.preventDefault();

  return (
    <div className="photo-gallery-viewer">
      <div className="zoom-controls">
        <button type="button" onClick={() => setZoom(z => Math.max(0.5, z - 0.25))}>-</button>
        <button type="button" onClick={handleReset}>Reset</button>
        <button type="button" onClick={() => setZoom(z => Math.min(5, z + 0.25))}>+</button>
      </div>
      <div
        className="photo-viewer-container"
        style={{ width, height, cursor: dragging ? 'grabbing' : 'grab' }}
        onWheel={handleWheel}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
      >
        <img
          src={image}
          alt=""
          style={{
            transform: `scale(${zoom}) translate(${pos.x/zoom}px, ${pos.y/zoom}px)`,
            transition: dragging ? 'none' : 'transform 0.2s',
            width: `${width}px`,
            height: `${height}px`,
            objectFit: 'contain',
            userSelect: 'none',
            pointerEvents: 'none'
          }}
          draggable={false}
          onDragStart={preventDragHandler}
        />
      </div>
    </div>
  );
}
