import React, { useState, useEffect } from 'react';

function AdminDashboard() {
    const [grievances, setGrievances] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchGrievances = async () => {
            // Retrieve the auth token from local storage
            const token = localStorage.getItem('token');

            if (!token) {
                setError('No authentication token found. Please log in.');
                setLoading(false);
                return;
            }

            try {
                const response = await fetch('/api/admin/grievances/', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                        // Include the JWT token in the Authorization header
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (!response.ok) {
                    // If the server responds with an error (like 403 Forbidden)
                    throw new Error('You do not have permission to view this page.');
                }

                const data = await response.json();
                setGrievances(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchGrievances();
    }, []); // The empty array ensures this effect runs only once on component mount

    if (loading) {
        return <div>Loading grievances...</div>;
    }

    if (error) {
        return <div style={{ color: 'red' }}>Error: {error}</div>;
    }

    return (
        <div>
            <h2>Admin Dashboard: All Grievances</h2>
            <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Description</th>
                        <th>Status</th>
                        <th>Submitted By</th>
                    </tr>
                </thead>
                <tbody>
                    {grievances.map(grievance => (
                        <tr key={grievance.id}>
                            <td>{grievance.id}</td>
                            <td>{grievance.title}</td>
                            <td>{grievance.description}</td>
                            <td>{grievance.status}</td>
                            <td>User ID: {grievance.user}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default AdminDashboard;
