// In src/pages/RegisterPage.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api'; // <-- IMPORT OUR API CLIENT

function RegisterPage() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [password1, setPassword2] = useState('');
  const navigate = useNavigate();

  const handleRegister = (event) => {
    event.preventDefault();
    // Use 'api' and the shortened URL
    api.post('auth/registration/', {
      username,
      email,
      password,
      password1
    })
    .then(response => {
      console.log('Registration successful:', response.data);
      navigate('/login');
    })
    .catch(error => {
      console.error('Registration error!', error.response.data);
    });
  };

  return (
    <div>
      <h2>Register</h2>
      <form onSubmit={handleRegister}>
        <div><input type="text" value={username} onChange={e => setUsername(e.target.value)} placeholder="Username" required /></div>
        <div><input type="email" value={email} onChange={e => setEmail(e.target.value)} placeholder="Email" required /></div>
        <div><input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" required /></div>
        <div><input type="password" value={password2} onChange={e => setPassword2(e.target.value)} placeholder="Confirm Password" required /></div>
        <button type="submit">Register</button>
      </form>
    </div>
  );
}

export default RegisterPage;
