import React, { useState, useEffect, useContext } from 'react';
import apiClient from '../apiClient';
import { AuthContext } from '../context/AuthContext';
import { Link } from 'react-router-dom';
import '../App.css';

const HomePage = () => {
  const { authToken, isStaff } = useContext(AuthContext);
  const [grievances, setGrievances] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [reopenReasons, setReopenReasons] = useState({});
  const [showReopenInputFor, setShowReopenInputFor] = useState(null);

  useEffect(() => {
    if (!authToken) {
      setGrievances([]);
      setLoading(false);
      return;
    }
    const fetchGrievances = async () => {
      try {
        setLoading(true);
        setError(null);
        const response = await apiClient.get('grievances/');
        setGrievances(response.data);
      } catch (err) {
        setError("Could not load your grievances. Please try again later.");
      } finally {
        setLoading(false);
      }
    };
    fetchGrievances();
  }, [authToken]);

  const handleReopen = async (id, reason) => {
    if (!reason || !reason.trim()) {
      alert('Please provide a reason for reopening.');
      return;
    }
    try {
      await apiClient.post(`grievances/${id}/reopen/`, { reason });
      // Refresh grievances to update UI
      const response = await apiClient.get('grievances/');
      setGrievances(response.data);
      setShowReopenInputFor(null);
      setReopenReasons({ ...reopenReasons, [id]: '' });
    } catch (err) {
      alert('Failed to reopen grievance.');
    }
  };

  // Protect homepage for admins
  if (isStaff) {
    return <h2>Admins do not have access to the user homepage.</h2>;
  }

  if (!authToken) return <h2>Please log in to view grievances.</h2>;
  if (loading) return <h2>Loading grievances...</h2>;
  if (error) return <h2 style={{ color: 'red' }}>{error}</h2>;

  return (
    <div>
      <h1>Your Submitted Grievances</h1>
      {grievances.length === 0 && <p>You have not submitted any grievances yet.</p>}
      {grievances.map(grievance => (
        <div key={grievance.id} style={{ marginBottom: 20 }}>
          <Link
            to={`/user/grievances/${grievance.id}`}
            className="grievance-container"
            style={{ textDecoration: 'none', color: 'inherit', display: 'block' }}
          >
            <h2>{grievance.title} ({grievance.status})</h2>
            <p>{grievance.description}</p>
            
          </Link>

          
        </div>
      ))}
    </div>
  );
};

export default HomePage;
