// In src/pages/HomePage.js

import React, { useState, useEffect } from 'react';
import api from '../api';
import GrievanceForm from '../components/GrievanceForm';
import UpdateForm from '../components/UpdateForm';
import '../App.css';

// Accept the new 'isStaff' prop
function HomePage({ token, isStaff }) {
  const [grievances, setGrievances] = useState([]);
  const [editingId, setEditingId] = useState(null);

  const fetchGrievances = () => {
    api.get('grievances/')
      .then(response => {
        setGrievances(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the grievances!", error);
        if (error.response && error.response.status === 401) {
          console.log("Unauthorized, token might be invalid or expired.");
        }
      });
  };

  useEffect(() => {
    if (token) {
      fetchGrievances();
    } else {
      setGrievances([]);
    }
  }, [token]);

  const handleDelete = (id) => {
    api.delete(`grievances/${id}/`)
      .then(() => {
        fetchGrievances();
      })
      .catch(error => {
        console.error("There was an error deleting the grievance!", error);
      });
  };

  const handleUpdate = () => {
    setEditingId(null);
    fetchGrievances();
  };

  if (!token) {
    return <h2>Please log in to view grievances.</h2>;
  }

  return (
    <div>
      {/* vvv THIS IS THE CHANGE vvv */}
      {/* Only show the form if the user is NOT staff */}
      {!isStaff && (
        <>
          <GrievanceForm onGrievanceCreated={fetchGrievances} />
          <hr style={{ width: '80%', margin: '40px 0' }} />
        </>
      )}

      <h1>{isStaff ? "Your Submitted Grievances" : "Grievance List"}</h1>
      {grievances.map(grievance => (
         <div key={grievance.id} style={{ border: '1px solid white', margin: '10px', padding: '10px' }}>
         { editingId === grievance.id ? (
           <UpdateForm 
             grievance={grievance} 
             onUpdate={handleUpdate} 
             onCancel={() => setEditingId(null)} 
           />
         ) : (
           <div>
             <h2>{grievance.title}</h2>
             <p>{grievance.description}</p>
             <p>Status: {grievance.status}</p>
             {/* Display owner, useful for admins on their own page too */}
             <p><b>Submitted by:</b> {grievance.owner}</p>
             <button onClick={() => setEditingId(grievance.id)}>Edit</button>
             <button onClick={() => handleDelete(grievance.id)} style={{ marginLeft: '10px', backgroundColor: 'darkred', color: 'white' }}>
               Delete
             </button>
           </div>
         )}
       </div>
      ))}
    </div>
  );
}

export default HomePage;
