// In src/App.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import GrievanceForm from './components/GrievanceForm';
import './App.css';

function App() {
  const [grievances, setGrievances] = useState([]);

  const fetchGrievances = () => {
    axios.get('http://127.0.0.1:8000/api/grievances/')
      .then(response => {
        setGrievances(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the grievances!", error);
      });
  };

  useEffect(() => {
    fetchGrievances();
  }, []);

  // --- ADD THIS DELETE HANDLER ---
  const handleDelete = (id) => {
    axios.delete(`http://127.0.0.1:8000/api/grievances/${id}/`)
      .then(response => {
        console.log('Grievance deleted:', id);
        // After deleting, refresh the list of grievances
        fetchGrievances();
      })
      .catch(error => {
        console.error("There was an error deleting the grievance!", error);
      });
  };
  // --- END OF NEW CODE ---

  return (
    <div className="App">
      <header className="App-header">
        <GrievanceForm onGrievanceCreated={fetchGrievances} />

        <hr style={{ width: '80%', margin: '40px 0' }} />

        <h1>Grievance List</h1>
        <div>
          {grievances.map(grievance => (
            <div key={grievance.id} style={{ border: '1px solid white', margin: '10px', padding: '10px' }}>
              <h2>{grievance.title}</h2>
              <p>{grievance.description}</p>
              <p>Status: {grievance.status}</p>

              {/* --- ADD THIS DELETE BUTTON --- */}
              <button 
                onClick={() => handleDelete(grievance.id)} 
                style={{ backgroundColor: 'darkred', color: 'white' }}
              >
                Delete
              </button>
              {/* --- END OF NEW CODE --- */}

            </div>
          ))}
        </div>
      </header>
    </div>
  );
}

export default App;
