import React, { useState, useEffect } from 'react';
import axios from 'axios';
import GrievanceAdminEdit from '../components/GrievanceAdminEdit';

function AdminPage() {
    const [grievances, setGrievances] = useState([]);
    const [selectedGrievanceId, setSelectedGrievanceId] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const token = localStorage.getItem('token');

    useEffect(() => {
        const fetchGrievances = async () => {
            try {
                setLoading(true);
                const response = await axios.get('/api/admin/grievances/', {
                    headers: { Authorization: `Token ${token}` }
                });
                setGrievances(response.data);
                setError(null);
            } catch (err) {
                setError('Failed to load grievances');
            } finally {
                setLoading(false);
            }
        };
        fetchGrievances();
    }, [token]);

    const onUpdateSuccess = () => {
        setSelectedGrievanceId(null);
        // Refresh grievance list
        setLoading(true);
        axios.get('/api/admin/grievances/', {
            headers: { Authorization: `Token ${token}` }
        }).then(res => {
            setGrievances(res.data);
            setLoading(false);
        });
    };

    if (loading) return <div>Loading grievances...</div>;
    if (error) return <div style={{ color: 'red' }}>{error}</div>;

    if (selectedGrievanceId) {
        return (
            <div>
                <button onClick={() => setSelectedGrievanceId(null)}>Back to List</button>
                <GrievanceAdminEdit 
                    grievanceId={selectedGrievanceId} 
                    authToken={token} 
                    onUpdateSuccess={onUpdateSuccess} 
                />
            </div>
        );
    }

    return (
        <div>
            <h2>Admin Dashboard - Grievances</h2>
            <ul>
                {grievances.map(g => (
                    <li key={g.id} style={{ marginBottom: '10px', cursor: 'pointer' }} 
                        onClick={() => setSelectedGrievanceId(g.id)}>
                        {g.title} - Status: {g.status}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default AdminPage;
