import React, { useState, useEffect } from 'react';
import axios from 'axios';

const GrievanceAdminEdit = ({ grievanceId, authToken, onUpdateSuccess }) => {
    const [grievance, setGrievance] = useState(null);
    const [status, setStatus] = useState('');
    const [resolutionNotes, setResolutionNotes] = useState('');
    const [signedDocument, setSignedDocument] = useState(null);
    const [resolutionImage, setResolutionImage] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Fetch grievance details by ID
        const fetchGrievance = async () => {
            try {
                setLoading(true);
                const response = await axios.get(`/api/admin/grievances/${grievanceId}/`, {
                    headers: { Authorization: `Token ${authToken}` }
                });
                setGrievance(response.data);
                setStatus(response.data.status || '');
                setResolutionNotes(response.data.resolution_notes || '');
            } catch (err) {
                setError('Failed to load grievance details.');
            } finally {
                setLoading(false);
            }
        };
        fetchGrievance();
    }, [grievanceId, authToken]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        const formData = new FormData();
        formData.append('status', status);
        formData.append('resolution_notes', resolutionNotes);

        if (signedDocument) {
            formData.append('signed_document', signedDocument);
        }
        if (resolutionImage) {
            formData.append('resolution_image', resolutionImage);
        }

        try {
            await axios.patch(`/api/admin/grievances/${grievanceId}/`, formData, {
                headers: {
                    Authorization: `Token ${authToken}`,
                    'Content-Type': 'multipart/form-data'
                }
            });
            onUpdateSuccess();
        } catch (err) {
            setError('Failed to update grievance.');
        }
    };

    if (loading) return <p>Loading grievance details...</p>;
    if (error) return <p style={{ color: 'red' }}>{error}</p>;
    if (!grievance) return <p>No grievance found.</p>;

    return (
        <form onSubmit={handleSubmit} style={{ maxWidth: '600px' }}>
            <h2>Edit Grievance: {grievance.title}</h2>
            <div>
                <label>Status:</label>
                <select value={status} onChange={e => setStatus(e.target.value)} required>
                    <option value="Pending">Pending</option>
                    <option value="In Progress">In Progress</option>
                    <option value="Resolved">Resolved</option>
                    <option value="Rejected">Rejected</option>
                    <option value="Closed">Closed</option>
                </select>
            </div>
            <div>
                <label>Resolution Notes:</label><br/>
                <textarea
                    value={resolutionNotes}
                    onChange={e => setResolutionNotes(e.target.value)}
                    rows={5}
                    style={{ width: '100%' }}
                />
            </div>
            <div>
                <label>Upload Signed Document:</label><br/>
                <input
                    type="file"
                    accept=".pdf,.doc,.docx"
                    onChange={e => setSignedDocument(e.target.files[0])}
                />
            </div>
            <div>
                <label>Upload Resolution Image:</label><br/>
                <input
                    type="file"
                    accept="image/*"
                    onChange={e => setResolutionImage(e.target.files[0])}
                />
            </div>
            <button type="submit" style={{ marginTop: '10px' }}>Save Updates</button>
        </form>
    );
};

export default GrievanceAdminEdit;
