// In src/pages/LoginPage.js

import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api';
import { AuthContext } from '../context/AuthContext';

const LoginPage = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);
    const navigate = useNavigate();

  const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:8000/api/auth/login/', {
                username,
                password,
            });
            // Call the context's login function with the response data
            login(response.data);
            
            // Redirect based on the role
            if (response.data.is_staff) {
                navigate('/admin');
            } else {
                navigate('/');
            }
        } catch (err) {
            setError('Invalid credentials. Please try again.');
        }
    };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div><input type="text" value={username} onChange={e => setUsername(e.target.value)} placeholder="Username" required /></div>
        <div><input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" required /></div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default LoginPage;
