// In src/pages/HomePage.js

import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios'; // We will use axios directly for clarity
import { AuthContext } from '../context/AuthContext'; // Import our context
import '../App.css';

const HomePage = () => {
    // Get the user's token and role from the AuthContext
    const { authToken, isStaff } = useContext(AuthContext);

    const [grievances, setGrievances] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // This useEffect will run whenever the authToken changes
    useEffect(() => {
        const fetchGrievances = async () => {
            // Only fetch if the user is actually logged in
            if (!authToken) {
                setLoading(false);
                return;
            }

            try {
                setLoading(true);
                setError(null);
                const response = await axios.get('http://127.0.0.1:8000/api/grievances/', {
                    headers: {
                        'Authorization': `Token ${authToken}`
                    }
                });
                setGrievances(response.data);
            } catch (err) {
                console.error("Failed to fetch grievances:", err);
                setError("Could not load your grievances. Please try again later.");
            } finally {
                setLoading(false);
            }
        };

        fetchGrievances();
    }, [authToken]); // The dependency array ensures this runs when the user logs in/out

    // --- The "Reopen" functionality remains, as users can reopen their own grievances ---
    const handleReopen = async (id) => {
        try {
            await axios.patch(`http://127.0.0.1:8000/api/grievances/${id}/`, { status: 'Reopened' }, {
                headers: { 'Authorization': `Token ${authToken}` }
            });
            // Refetch the grievances to show the updated status
            const response = await axios.get('http://127.0.0.1:8000/api/grievances/', {
                headers: { 'Authorization': `Token ${authToken}` }
            });
            setGrievances(response.data);
        } catch (err) {
            alert("Failed to reopen grievance.");
        }
    };

    // --- Render different content based on the loading/error/login state ---
    if (loading) {
        return <h2>Loading grievances...</h2>;
    }

    if (error) {
        return <h2 style={{ color: 'red' }}>{error}</h2>;
    }

    if (!authToken) {
        return <h2>Please log in to view grievances.</h2>;
    }
    
    // The main display for when the user is logged in and data is loaded
    return (
        <div>
            <h1>Your Submitted Grievances</h1>
            {grievances.length === 0 && <p>You have not submitted any grievances yet.</p>}
            
            {grievances.map(grievance => (
                <div key={grievance.id} className="grievance-container">
                    <h2>{grievance.title} ({grievance.status})</h2>
                    <p>{grievance.description}</p>
                    <p><b>Department:</b> {grievance.department_name || 'Not Assigned'}</p>
                    <p><b>Category:</b> {grievance.category_name || 'Not Assigned'}</p>
                    <p><i>Last updated: {new Date(grievance.updated_at).toLocaleString()}</i></p>

                    {/* 
                      * We removed the "Edit" button for regular users because we made grievances
                      * read-only for them in the backend. This aligns the UI with the API rules.
                    */}

                    {['Resolved', 'Closed'].includes(grievance.status) && !isStaff && (
                        <button onClick={() => handleReopen(grievance.id)} style={{ backgroundColor: '#006400', color: 'white' }}>
                            Reopen Grievance
                        </button>
                    )}
                </div>
            ))}
        </div>
    );
};

export default HomePage;
