// In src/components/GrievanceForm.js

// --- FIX: A single, clean import block ---
import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
// -----------------------------------------

const GrievanceForm = () => {
    // Get the auth token from our context
    const { authToken } = useContext(AuthContext);

    // State for the form fields
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [department, setDepartment] = useState('');
    const [category, setCategory] = useState('');

    // State for the dropdown options
    const [departments, setDepartments] = useState([]);
    const [categories, setCategories] = useState([]);

    // State for loading and error messages
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const navigate = useNavigate();

    // This useEffect fetches the data for the dropdowns when the component first loads
    useEffect(() => {
        const fetchData = async () => {
            try {
                const deptResponse = await axios.get('http://127.0.0.1:8000/api/departments/');
                setDepartments(deptResponse.data);

                const catResponse = await axios.get('http://127.0.0.1:8000/api/categories/');
                setCategories(catResponse.data);
            } catch (err) {
                setError('Failed to load form data. Please refresh the page.');
            }
        };
        fetchData();
    }, []);

    // This function handles the form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setLoading(true);

        const grievanceData = {
            title,
            description,
            department: department || null, // Send null if empty
            category: category || null,     // Send null if empty
        };

        try {
            await axios.post('http://127.0.0.1:8000/api/grievances/', grievanceData, {
                headers: { 'Authorization': `Token ${authToken}` }
            });
            // On success, go to the homepage/dashboard
            navigate('/');
        } catch (err) {
            // This is where we will see the 400 Bad Request error message
            if (err.response && err.response.data) {
                // Convert the error object to a readable string
                const errorMessages = Object.entries(err.response.data)
                    .map(([field, messages]) => `${field}: ${messages.join(' ')}`)
                    .join(' | ');
                setError(errorMessages);
            } else {
                setError('An unexpected error occurred. Please try again.');
            }
        } finally {
            setLoading(false);
        }
    };
    
    // This calculates which categories are selectable (not parents)
    const getSelectableCategories = () => {
        if (categories.length === 0) return [];
        const parentIds = new Set(categories.map(cat => cat.parent).filter(id => id !== null));
        return categories.filter(cat => !parentIds.has(cat.id));
    };

    return (
        <div className="form-container">
            <h2>Submit a New Grievance</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="title">Title</label>
                    <input type="text" id="title" value={title} onChange={(e) => setTitle(e.target.value)} required />
                </div>
                <div className="form-group">
                    <label htmlFor="description">Description</label>
                    <textarea id="description" value={description} onChange={(e) => setDescription(e.target.value)} required />
                </div>
                <div className="form-group">
                    <label htmlFor="department">Department</label>
                    <select id="department" value={department} onChange={(e) => setDepartment(e.target.value)} required>
                        <option value="">Select a Department</option>
                        {departments.map(dept => <option key={dept.id} value={dept.id}>{dept.name}</option>)}
                    </select>
                </div>
                <div className="form-group">
                    <label htmlFor="category">Category</label>
                    <select id="category" value={category} onChange={(e) => setCategory(e.target.value)} required>
                        <option value="">Select a Specific Category</option>
                        {getSelectableCategories().map(cat => <option key={cat.id} value={cat.id}>{cat.full_path}</option>)}
                    </select>
                </div>
                <button type="submit" disabled={loading}>{loading ? 'Submitting...' : 'Submit Grievance'}</button>
                {error && <p style={{color: 'red'}}>{error}</p>}
            </form>
        </div>
    );
};

export default GrievanceForm;
