// In src/App.js

import React, { useContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';

// Import the context and provider
import { AuthContext, AuthProvider } from './context/AuthContext';

// Import all your page components
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import AdminPage from './pages/AdminPage';
import GrievanceForm from './components/GrievanceForm';
import './App.css';

// This component holds the main structure of your app and uses the context
function AppContent() {
    const { isAuthenticated, isStaff, logout } = useContext(AuthContext);

    return (
        <Router>
            <div className="App">
                <header className="App-header">
                    <nav style={{ marginBottom: '20px', fontSize: '1.2rem' }}>
                        {isAuthenticated ? (
                            <>
                                <Link to="/" style={{ marginRight: '15px' }}>Home</Link>
                                {isStaff && (
                                    <Link to="/admin" style={{ marginRight: '15px', color: 'cyan' }}>Admin</Link>
                                )}
                                <Link to="/submit-grievance" style={{ marginRight: '15px' }}>Submit Grievance</Link>
                                <button onClick={logout}>Logout</button>
                            </>
                        ) : (
                            <>
                                <Link to="/login" style={{ marginRight: '15px' }}>Login</Link>
                                <Link to="/register">Register</Link>
                            </>
                        )}
                    </nav>
                    <Routes>
                        {/* Public routes */}
                        <Route path="/login" element={<LoginPage />} />
                        <Route path="/register" element={<RegisterPage />} />

                        {/* Protected routes that redirect to login if not authenticated */}
                        <Route path="/submit-grievance" element={isAuthenticated ? <GrievanceForm /> : <Navigate to="/login" />} />
                        <Route path="/" element={isAuthenticated ? <HomePage /> : <Navigate to="/login" />} />

                        {/* Admin route that requires both login and staff status */}
                        <Route path="/admin" element={isAuthenticated && isStaff ? <AdminPage /> : <Navigate to="/" />} />
                    </Routes>
                </header>
            </div>
        </Router>
    );
}

// The main App component's ONLY job is to wrap everything in the AuthProvider
function App() {
    return (
        <AuthProvider>
            <AppContent />
        </AuthProvider>
    );
}

export default App;
