import React, { useState, useEffect, useContext } from 'react';
import AdminGrievanceEditor from './AdminGrievanceEditor';
import { AuthContext } from '../context/AuthContext';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function AdminDashboard() {
  const { authToken } = useContext(AuthContext);
  const [grievances, setGrievances] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const navigate = useNavigate();

  // Try context first, fallback to localStorage
  const token = authToken || localStorage.getItem('token');

  // Redirect IMMEDIATELY when token becomes missing (handles logout)
  useEffect(() => {
    if (!token) {
      navigate('/login', { replace: true }); // force redirect on logout
    }
  }, [token, navigate]);

  const fetchGrievances = async () => {
    try {
      setLoading(true);
      const res = await axios.get(
        'http://127.0.0.1:8000/api/admin/grievances/',
        { headers: { Authorization: `Token ${token}` } }
      );
      setGrievances(res.data);
      setError(null);
    } catch (error) {
      if (
        error.response &&
        (error.response.status === 401 || error.response.status === 403)
      ) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login', { replace: true });
        return;
      }
      setError('Failed to load grievances.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // Only fetch if token exists, prevents API calls after logout
    if (token) {
      fetchGrievances();
    }
    // eslint-disable-next-line
  }, [token]);

  const handleUpdateSuccess = () => {
    setEditingId(null);
    fetchGrievances();
  };

  if (!token) return null; // Protect against unwanted render

  if (loading) return <p>Loading grievances...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  if (editingId)
    return (
      <div>
        <button onClick={() => setEditingId(null)}>Back to Dashboard</button>
        <AdminGrievanceEditor
          grievanceId={editingId}
          onUpdateSuccess={handleUpdateSuccess}
        />
      </div>
    );

  return (
    <div>
      <h2>Admin Dashboard - Grievances</h2>
      <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>Title</th>
            <th>Status</th>
            <th>Edit</th>
          </tr>
        </thead>
        <tbody>
          {grievances.map(g => (
            <tr key={g.id}>
              <td>{g.title}</td>
              <td>{g.status}</td>
              <td>
                <button onClick={() => setEditingId(g.id)}>Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
