import React, { useState, useEffect } from 'react';
import AdminGrievanceEdit from './AdminGrievanceEditor';
import axios from 'axios';

export default function AdminDashboard() {
  const [grievances, setGrievances] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('token');

  const fetchGrievances = async () => {
    try {
      setLoading(true);
      const res = await axios.get('/api/admin/grievances/', {
        headers: { Authorization: `Token ${token}` },
      });
      setGrievances(res.data);
      setError(null);
    } catch {
      setError('Failed to load grievances.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchGrievances();
    // eslint-disable-next-line
  }, [token]);

  const handleUpdateSuccess = () => {
    setEditingId(null);
    fetchGrievances();
  };

  if (loading) return <p>Loading grievances...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  if (editingId)
    return (
      <div>
        <button onClick={() => setEditingId(null)}>Back to Dashboard</button>
        <AdminGrievanceEdit
          grievanceId={editingId}
          authToken={token}
          onUpdateSuccess={handleUpdateSuccess}
        />
      </div>
    );

  return (
    <div>
      <h2>Admin Dashboard - Grievances</h2>
      <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>Title</th>
            <th>Status</th>
            <th>Edit</th>
          </tr>
        </thead>
        <tbody>
          {grievances.map(g => (
            <tr key={g.id}>
              <td>{g.title}</td>
              <td>{g.status}</td>
              <td>
                <button onClick={() => setEditingId(g.id)}>Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
