import React, { useState, useEffect, useContext } from 'react';
import apiClient from '../apiClient';
import { AuthContext } from '../context/AuthContext';
import { Link } from 'react-router-dom';
import '../App.css';

const HomePage = () => {
  const { authToken, isStaff } = useContext(AuthContext);
  const [grievances, setGrievances] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [reopenReasons, setReopenReasons] = useState({});
  const [showReopenInputFor, setShowReopenInputFor] = useState(null);

  useEffect(() => {
    if (!authToken) {
      setGrievances([]);
      setLoading(false);
      return;
    }
    const fetchGrievances = async () => {
      try {
        setLoading(true);
        setError(null);
        const response = await apiClient.get('grievances/');
        setGrievances(response.data);
      } catch (err) {
        setError("Could not load your grievances. Please try again later.");
      } finally {
        setLoading(false);
      }
    };
    fetchGrievances();
  }, [authToken]);

  const handleReopen = async (id, reason) => {
    if (!reason || !reason.trim()) {
      alert('Please provide a reason for reopening.');
      return;
    }
    try {
      await apiClient.post(`grievances/${id}/reopen/`, { reason });
      // Refresh grievances to update UI
      const response = await apiClient.get('grievances/');
      setGrievances(response.data);
      setShowReopenInputFor(null);
      setReopenReasons({ ...reopenReasons, [id]: '' });
    } catch (err) {
      alert('Failed to reopen grievance.');
    }
  };

  // ----------- PROTECT HOMEPAGE FOR ADMINS -----------
  if (isStaff) {
    return <h2>Admins do not have access to the user homepage.</h2>;
  }
  // ---------------------------------------------------

  if (!authToken) return <h2>Please log in to view grievances.</h2>;
  if (loading) return <h2>Loading grievances...</h2>;
  if (error) return <h2 style={{ color: 'red' }}>{error}</h2>;

  return (
    <div>
      <h1>Your Submitted Grievances</h1>
      {grievances.length === 0 && <p>You have not submitted any grievances yet.</p>}
      {grievances.map(grievance => (
  <div key={grievance.id} className="grievance-container">
    <h2>
      <Link to={`/user/grievances/${grievance.id}`}>
        {grievance.title} ({grievance.status})
      </Link>
    </h2>
    <p>{grievance.description}</p>
    {/* <p>
      <b>Department: </b>
      {grievance.department_name === 'Grievance Triage'
        ? 'In Review'
        : (grievance.department_name || 'Not Assigned')}
    </p>
    <p>
      <b>Category: </b>
      {grievance.category_name || 'In Review'}
    </p>
    <p>
      <i>Last updated: {new Date(grievance.updated_at).toLocaleString()}</i>
    </p> */}
          {/* Show reopen only if status is Resolved, Closed, or Rejected */}
          {['Resolved', 'Closed', 'Rejected'].includes(grievance.status) && (
            <>
              {showReopenInputFor === grievance.id ? (
                <>
                  <textarea
                    rows={3}
                    style={{ width: '100%', marginBottom: '8px' }}
                    placeholder="Enter reason to reopen"
                    value={reopenReasons[grievance.id] || ''}
                    onChange={e =>
                      setReopenReasons({
                        ...reopenReasons,
                        [grievance.id]: e.target.value,
                      })
                    }
                  />
                  <button
                    onClick={() =>
                      handleReopen(grievance.id, reopenReasons[grievance.id] || '')
                    }
                    style={{ backgroundColor: '#006400', color: 'white' }}
                  >
                    Submit
                  </button>
                  <button onClick={() => setShowReopenInputFor(null)}>Cancel</button>
                </>
              ) : (
                <button
                  onClick={() => setShowReopenInputFor(grievance.id)}
                  style={{ backgroundColor: '#006400', color: 'white' }}
                >
                  Reopen Grievance
                </button>
              )}
            </>
          )}
        </div>
      ))}
    </div>
  );
};

export default HomePage;
