import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const { login, userGroups } = useContext(AuthContext); // add userGroups here


  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/auth/login/', {
        username,
        password,
      });

      // Debug: Print the backend response
      console.log('Login response:', response.data);

      // Ensure response includes token and user with is_staff
      if (!response.data.user || typeof response.data.user.is_staff === "undefined") {
        setError("Login failed: invalid backend response.");
        return;
      }

      // Set authToken and user info in AuthContext
await login(response.data);

// Decide redirect based on groups in the updated auth.user
const groups = (response.data.user.groups || []);

// If you trust /api/me/ more, read from context later; for now:
if (groups.includes('DEPARTMENT_ADMIN') || groups.includes('HIGHER_AUTHORITY')) {
  navigate('/admin');
} else if (groups.includes('TRIAGE_USER')) {
  navigate('/triage');
} else {
  navigate('/');
}

    } catch (err) {
      setError('Invalid credentials. Please try again.');
      console.error("Login error:", err);
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="text"
            value={username}
            onChange={e => setUsername(e.target.value)}
            placeholder="Username"
            required
          />
        </div>
        <div>
          <input
            type="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            placeholder="Password"
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default LoginPage;
