// In src/pages/HomePage.js

import React, { useState, useEffect } from 'react';
import api from '../api';
import GrievanceForm from '../components/GrievanceForm';
import UpdateForm from '../components/UpdateForm';
import '../App.css';

function HomePage({ token, isStaff }) {
  const [grievances, setGrievances] = useState([]);
  const [editingId, setEditingId] = useState(null);

  const fetchGrievances = () => {
    if (token) {
      api.get('grievances/')
        .then(response => setGrievances(response.data))
        .catch(console.error);
    } else {
      setGrievances([]);
    }
  };

  useEffect(fetchGrievances, [token]);

  const handleReopen = (id) => {
    api.patch(`grievances/${id}/`, { status: 'Reopened' })
      .then(fetchGrievances)
      .catch(err => alert("Failed to reopen grievance."));
  };

  const handleUpdate = () => {
    setEditingId(null);
    fetchGrievances();
  };

  if (!token) return <h2>Please log in to view grievances.</h2>;

  return (
    <div>
      {!isStaff && (
        <>
          <GrievanceForm onGrievanceCreated={fetchGrievances} />
          <hr style={{ width: '80%', margin: '40px 0' }} />
        </>
      )}

      <h1>Your Submitted Grievances</h1>
      {grievances.map(grievance => (
        <div key={grievance.id} className="grievance-container">
          { editingId === grievance.id ? (
            <UpdateForm grievance={grievance} onUpdate={handleUpdate} onCancel={() => setEditingId(null)} />
          ) : (
            <div>
              <h2>{grievance.title} ({grievance.status})</h2>
              <p>{grievance.description}</p>
              <p><b>Department:</b> {grievance.department_name || 'Not Assigned'}</p>
              <p><i>Last updated: {new Date(grievance.updated_at).toLocaleString()}</i></p>

              {/* Conditional Buttons */}
              {['Pending', 'Reopened', 'In Progress'].includes(grievance.status) && (
                <button onClick={() => setEditingId(grievance.id)}>Edit Title/Description</button>
              )}
              
              {['Resolved', 'Closed'].includes(grievance.status) && (
                <button onClick={() => handleReopen(grievance.id)} style={{ backgroundColor: '#006400', color: 'white' }}>
                  Reopen Grievance
                </button>
              )}
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default HomePage;
