// In src/components/GrievanceForm.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const GrievanceForm = () => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [department, setDepartment] = useState('');
    const [category, setCategory] = useState('');

    const [departments, setDepartments] = useState([]);
    const [categories, setCategories] = useState([]);

    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const deptResponse = await axios.get('http://127.0.0.1:8000/api/departments/');
                setDepartments(deptResponse.data);

                const catResponse = await axios.get('http://127.0.0.1:8000/api/categories/');
                setCategories(catResponse.data);
            } catch (err) {
                console.error("Error fetching data:", err);
                setError('Failed to load form data. Please try refreshing the page.');
            }
        };
        fetchData();
    }, []);

    // --- NEW: Implemented the submit handler ---
    const handleSubmit = async (e) => {
        e.preventDefault(); // Prevents the browser from reloading the page
        setError(null);
        setLoading(true);

        // Get the auth token from local storage
        const token = localStorage.getItem('token');
        if (!token) {
            setError('You must be logged in to submit a grievance.');
            setLoading(false);
            return;
        }

        const grievanceData = {
            title,
            description,
            department, // This is the department ID from the state
            category,   // This is the category ID from the state
        };

        try {
            await axios.post(
                'http://127.0.0.1:8000/api/grievances/',
                grievanceData,
                {
                    headers: {
                        'Authorization': `Token ${token}`
                    }
                }
            );
            // On success, navigate to the user's dashboard or a success page
            navigate('/dashboard'); // Or wherever you list the user's grievances
        } catch (err) {
            console.error("Error submitting grievance:", err);
            setError('Failed to submit grievance. Please check your inputs and try again.');
        } finally {
            setLoading(false);
        }
    };

     return (
        <div className="form-container">
            <h2>Submit a New Grievance</h2>
            <form onSubmit={handleSubmit}>
                {/* Title and Description fields are unchanged */}
                {/* Department dropdown is unchanged */}

                {/* --- Replace your old category dropdown with this one --- */}
                <div className="form-group">
                    <label htmlFor="category">Category</label>
                    <select
                        id="category"
                        value={category}
                        onChange={(e) => setCategory(e.target.value)}
                        required
                    >
                        <option value="">Select a Specific Category</option>
                        {
                            // 1. Get all IDs that are used as a parent
                            (() => {
                                const parentIds = new Set(categories.map(cat => cat.parent).filter(id => id !== null));
                                // 2. Filter to only show categories that are NOT parents (leaf nodes)
                                const selectableCategories = categories.filter(cat => !parentIds.has(cat.id));
                                // 3. Render the options using the full path
                                return selectableCategories.map(cat => (
                                    <option key={cat.id} value={cat.id}>
                                        {cat.full_path}
                                    </option>
                                ));
                            })()
                        }
                    </select>
                </div>
                {/* ------------------------------------------------------ */}

                <button type="submit" disabled={loading}>
                    {loading ? 'Submitting...' : 'Submit Grievance'}
                </button>
                {error && <p className="error-message">{error}</p>}
            </form>
        </div>
    );
};

export default GrievanceForm;
