// In src/pages/AdminPage.js

import React, { useState, useEffect } from 'react';
import api from '../api';
import AdminGrievanceEditor from '../components/AdminGrievanceEditor'; // <-- IMPORT

function AdminPage() {
  const [grievances, setGrievances] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [editingId, setEditingId] = useState(null); // Tracks which grievance is being edited
  const [error, setError] = useState('');

  const fetchData = () => {
    // Use Promise.all to fetch both sets of data concurrently
    Promise.all([
      api.get('admin/grievances/'),
      api.get('departments/')
    ])
    .then(([grievancesRes, departmentsRes]) => {
      setGrievances(grievancesRes.data);
      setDepartments(departmentsRes.data);
    })
    .catch(err => {
      console.error("Error fetching admin data!", err);
      if (err.response && err.response.status === 403) {
        setError('Access Denied: You do not have permission to view this page.');
      } else {
        setError('An error occurred while fetching data.');
      }
    });
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleUpdate = () => {
    setEditingId(null); // Close the editor form
    fetchData();      // And refresh all data from the server
  };

  if (error) {
    return <h2 style={{ color: 'red' }}>{error}</h2>;
  }

  return (
    <div>
      <h1>Admin Dashboard: All Grievances</h1>
      {grievances.map(grievance => (
        <div key={grievance.id} style={{ border: '1px solid white', margin: '10px', padding: '10px' }}>
          { editingId === grievance.id ? (
            // If this is the grievance being edited, show the editor
            <AdminGrievanceEditor
              grievance={grievance}
              departments={departments}
              onUpdate={handleUpdate}
              onCancel={() => setEditingId(null)}
            />
          ) : (
            // Otherwise, show the normal view
            <div>
              <h2>{grievance.title}</h2>
              <p>{grievance.description}</p>
              <p><b>Submitted by:</b> {grievance.owner}</p>
              <p><b>Status:</b> {grievance.status}</p>
              <p><b>Department:</b> {grievance.department_name || 'Not Assigned'}</p>
              <p><b>Last Updated:</b> {new Date(grievance.updated_at).toLocaleString()}</p>
              <button onClick={() => setEditingId(grievance.id)}>Manage</button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default AdminPage;
