import React, { useState, useEffect } from 'react';
import apiClient from '../apiClient';

export default function UserGrievanceDetail({ grievanceId }) {
  const [grievance, setGrievance] = useState(null);
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [reopenReason, setReopenReason] = useState('');
  const [reopenLoading, setReopenLoading] = useState(false);
  const [reopenError, setReopenError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const gRes = await apiClient.get(`grievances/${grievanceId}/`);
        setGrievance(gRes.data);
        const eRes = await apiClient.get(`grievances/${grievanceId}/events/`);
        setEvents(eRes.data);
        setError(null);
      } catch {
        setError('Failed to load grievance details.');
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [grievanceId]);

  const handleReopen = async () => {
    if (!reopenReason.trim()) {
      setReopenError('Please provide a reason for reopening.');
      return;
    }
    setReopenError(null);
    setReopenLoading(true);
    try {
      await apiClient.post(`grievances/${grievanceId}/reopen/`, { reason: reopenReason });
      setReopenReason('');
      // Refresh grievance and events after reopen
      const gRes = await apiClient.get(`grievances/${grievanceId}/`);
      setGrievance(gRes.data);
      const eRes = await apiClient.get(`grievances/${grievanceId}/events/`);
      setEvents(eRes.data);
    } finally {
      setReopenLoading(false);
    }
  };

  if (loading) return <p>Loading grievance details...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (!grievance) return null;
console.log('Grievance:', grievance);
  return (
    <div>
      <h2>Grievance Details</h2>
      <p><strong>Status:</strong> {grievance.status}</p>
      <p>
      <i>Last updated: {new Date(grievance.updated_at).toLocaleString()}</i>
    </p>
      <p><strong>Department:</strong> {grievance.department_name || (grievance.category?.department?.name) || 'N/A'}</p>
      <p><strong>Category:</strong> {grievance.category?.full_path || grievance.category_name || grievance.category?.name || 'N/A'}</p>
      <p><strong>Description:</strong> {grievance.description}</p>
      {/* New fields: location and uploaded image */}
      {grievance.location && (
        <p>
          <strong>Location:</strong> {grievance.location}
        </p>
      )}
      
      {grievance.resolution_image && (
        <div style={{ margin: '16px 0' }}>
          <strong>Uploaded Image:</strong><br />
          <img
            src={grievance.resolution_image}
            alt="Proof"
            style={{ maxWidth: '300px', border: '1px solid #ccc', marginTop: '8px' }}
          />
        </div>
      )}
      {['Resolved', 'Rejected'].includes(grievance.status) && (
        <div style={{ marginTop: 20 }}>
          <label htmlFor="reopenReason">Reason for Reopening:</label><br />
          <textarea
            id="reopenReason"
            rows="3"
            value={reopenReason}
            onChange={(e) => setReopenReason(e.target.value)}
            placeholder="Enter reason here..."
            style={{ width: '100%', marginBottom: '10px' }}
          />
          {reopenError && <p style={{ color: 'red' }}>{reopenError}</p>}
          <button onClick={handleReopen} disabled={reopenLoading}>
            {reopenLoading ? 'Reopening...' : 'Reopen Grievance'}
          </button>
        </div>
      )}
      <h3>Event Log</h3>
      <ul>
        {events.length === 0 && <li>No events yet.</li>}
        {events.map(ev => (
          <li key={ev.id}>
            [{new Date(ev.timestamp).toLocaleString()}] {ev.action} by {(ev.user && (ev.user.username || ev.user)) || 'System'}
            {ev.notes && <span>: {ev.notes}</span>}
          </li>
        ))}
      </ul>
    </div>
  );
}
