import React, { useEffect, useState } from 'react';
import axios from 'axios';
import GrievanceAdminEdit from '../components/AdminGrievanceEdit';


const AdminPage = () => {
  const [grievances, setGrievances] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const res = await axios.get('/api/admin/grievances/', {
          headers: { Authorization: `Token ${token}` }
        });
        setGrievances(res.data);
        setError(null);
      } catch {
        setError('Failed to load grievances.');
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [token]);

  const handleUpdateSuccess = () => {
    setSelectedId(null);
    // Refresh grievances list
    setLoading(true);
    axios.get('/api/admin/grievances/', {
      headers: { Authorization: `Token ${token}` }
    }).then(res => {
      setGrievances(res.data);
      setLoading(false);
    });
  };

  if (loading) return <p>Loading grievances...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  if (selectedId)
    return (
      <div>
        <button onClick={() => setSelectedId(null)}>Back to list</button>
        <GrievanceAdminEdit grievanceId={selectedId} authToken={token} onUpdateSuccess={handleUpdateSuccess} />
      </div>
    );

  return (
    <div>
      <h2>Admin Dashboard - Grievances</h2>
      <ul>
        {grievances.map(g => (
          <li key={g.id} onClick={() => setSelectedId(g.id)} style={{ cursor: 'pointer' }}>
            {g.title} - Status: {g.status}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminPage;
